void paint(int **b, int x, int y, int a) {
	if( y - a >= 0)
		b[y-a][x]++;
	if( y + a < 10)
		b[y+a][y]++;
	if( x - a >= 0)
		b[y][x-a]++;
	if( x + a < 10)
		b[y][x+a]++;
}

