    if (s == 1){
      small(x, y);
    }
    else if (s == 2){
      medium(x, y);
    }
    else if (s == 3){
      big(x, y);
    }
  }
  white = 0;
  max = 0;
  for (i = 2; i < 12; i++){
    for (j = 2; j < 12; j++){
      if (cell[i][j] == 0){
        white++;
      }
      else if (cell[i][j] > max){
        max = cell[i][j];
      }
    }
  }

  printf("%d %d\n", white, max);

  return (0);
}

