//真っ白な紙にインクを垂らしインク絵汚れていない部分の数を出力し、汚れる回数が多いエリアを出力する。
//落とすインク液は少しならば１、中ぐらいなら２、多いならば３とする。
#include<stdio.h>

void littleDrop(int,int,int *,int);
void midiumDrop(int,int *,int);
void largeDrop(int,int *);
void showArray(int [][10]);

void largeDrop(int center,int *tbl){
	if(center >= 20)//二つ上に配列があるか確認
		*(tbl - 20) += 1;
	if(center % 10 >= 2)//二つ左に配列があるか確認
		*(tbl - 2) += 1;
	if(center % 10 <= 7)//二つ右に配列があるか確認
		*(tbl + 2) += 1;
	if(center <= 80)//二つ下に配列がある確認
		*(tbl + 20) += 1;
}

void midiumDrop(int center,int *tbl ,int size){
//真ん中と八方に+1//
	if(center % 10 != 0 && center > 10)//左、上に配列があるか確認
		*(tbl - 11) += 1;
	if(center % 10 != 9 && center > 10)//右、上に配列があるか確認
		*(tbl - 9) += 1;
	if(center % 10 != 0 && center < 90)//左、下に配列があるか確認
		*(tbl + 9) += 1;
	if(center % 10 != 9 && center < 90)//右、下に配列があるか確認
		*(tbl + 11) += 1;
	if(size == 3)
		largeDrop(center,tbl);
}


void littleDrop(int x ,int y ,int *tbl,int size){
//インクを落とす場所は左上(0,0)から何番目かを求める。
	int center = x + y * 10;
//(0,0)から中央へと移動させる
	tbl = (tbl + center);

//真ん中に+1
	*(tbl) += 1;
//上に+1
	if(center >= 10)//上に配列があるか確認
		*(tbl - 10) += 1;
	if(center <= 90)//下に配列があるか確認
		*(tbl + 10) += 1;
	if(center % 10 != 0)//左に配列があるか確認
		*(tbl - 1) += 1;
	if(center % 10 != 9)//右に配列があるか確認
		*(tbl + 1) += 1;
	if(size >= 2)
		midiumDrop(center, tbl, size);
}

