import java.io.IOException;
import java.util.Scanner;

public class Main {
	static int[][] board = new int[10][10];

	public static void main(String[] args) throws IOException {
		// 宣言
		Scanner sc = new Scanner(System.in);
		String[] str;
		int count = 0;
		int max = 0;

		while (sc.hasNext()) {
			str = sc.nextLine().split(",");
			int x = Integer.parseInt(str[0]);
			int y = Integer.parseInt(str[1]);
			int size = Integer.parseInt(str[2]);
			if (size == 1) {
				small(x, y);
			} else if (size == 2) {
				middle(x, y);
			} else if (size == 3) {
				big(x, y);
			}
		}
		for (int xx = 0; xx < 10; xx++) {
			for (int yy = 0; yy < 10; yy++) {
				// 1個目の答え
				if (board[xx][yy] == 0) {
					count++;
				}
				// 2個目の答え
				if (max < board[xx][yy]) {
					max = board[xx][yy];
				}
			}
		}
		System.out.println(count);
		System.out.println(max);
	}

	// インク小　中心と上下左右1マス
	static void small(int x, int y) {
		paint(x, y);
		// 左右、上下のマスを塗る
		paint(x + 1, y);
		paint(x - 1, y);
		paint(x, y - 1);
		paint(x, y + 1);
	}
	// インク中　上下左右斜め9マス
	static void middle(int x, int y) {
		for (int yr = -1; yr <= 1; yr++) {
			for (int xr = -1; xr <= 1; xr++) {
				paint(x + xr, y + yr);
			}
		}
	}
	//　インク大　インク中＋上下左右2マス
	static void big(int x, int y) {
		middle(x, y);
		//左右、上下の2マス目を塗る
		paint(x, y - 2);
		paint(x, y + 2);
		paint(x + 2, y);
		paint(x - 2, y);
	}

	static void paint(int x, int y) {
		//範囲外にあたる場合は何もしない
		if (x < 0 || y < 0 || x >= 10 || y >= 10) {
			return;
		} else {
			board[x][y] += 1;
		}

	}

}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {

	/** 区切り文字 */
	static final String REGEX = ",";

	/** インキ滴の小 */
	static final int SMALL_SIZE = 1;

	/** インキ滴の中 */
	static final int MIDIAM_SIZE = 2;

	/** インキ滴の大 */
	static final int LARGE_SIZE = 3;

	/**
	 * @param args
	 * @throws IOException
	 */
	public static void main(String[] args) throws IOException {
		BufferedReader reader = new BufferedReader(new InputStreamReader(
				System.in));
		int[] field = new int[101];
		int x;
		int y;
		int size;
		while (true) {
			String line = reader.readLine();
			if (line == null || line.isEmpty()) {
				break;
			}
			String[] str = line.split(REGEX);
			x = Integer.valueOf(str[0]);
			y = Integer.valueOf(str[1]);
			size = Integer.valueOf(str[2]);

			switch (size) {
			case SMALL_SIZE:
				drowSmallArea(field, x, y);
				break;
			case MIDIAM_SIZE:
				drowSmallArea(field, x, y);
				drowMidiumArea(field, x, y);
				break;
			case LARGE_SIZE:
				drowSmallArea(field, x, y);
				drowMidiumArea(field, x, y);
				drowLargeArea(field, x, y);
				break;
			default:
			}
		}

		int count = 0;
		int max = 0;
		for (int i = 0; i < 100; i++) {
			if (field[i] == 0) {
				count++;
			}
			if (field[i] > max) {
				max = field[i];
			}
		}

		System.out.println(count);
		System.out.println(max);
	}

	/**
	 * x座標とy座標を受け取って、配列のインデックスを返却する。
	 *
	 * @param x
	 *            x座標
	 * @param y
	 *            y座標
	 * @return 配列のインデックス
	 */
	private static int calcIndex(int x, int y) {
		if (x < 0 || y < 0 || x > 9 || y > 9) {
			return 100;
		}
		return (x * 10) + y;
	}

	/**
	 * 小サイズ領域をインクで染める
	 *
	 * @param field
	 *            フィールド
	 * @param x
	 *            x座標
	 * @param y
	 *            y座標
	 */
	private static void drowSmallArea(int[] field, int x, int y) {
		field[calcIndex(x - 1, y)] += 1;
		field[calcIndex(x, y + 1)] += 1;
		field[calcIndex(x, y)] += 1;
		field[calcIndex(x, y - 1)] += 1;
		field[calcIndex(x + 1, y)] += 1;
	}

	/**
	 * 中サイズ領域をインクで染める
	 *
	 * @param field
	 *            フィールド
	 * @param x
	 *            x座標
	 * @param y
	 *            y座標
	 */
	private static void drowMidiumArea(int[] field, int x, int y) {
		field[calcIndex(x - 1, y + 1)] += 1;
		field[calcIndex(x - 1, y - 1)] += 1;
		field[calcIndex(x + 1, y + 1)] += 1;
		field[calcIndex(x + 1, y - 1)] += 1;
	}

	/**
	 * 大サイズ領域をインクで染める
	 *
	 * @param field
	 *            フィールド
	 * @param x
	 *            x座標
	 * @param y
	 *            y座標
	 */
	private static void drowLargeArea(int[] field, int x, int y) {
		field[calcIndex(x - 2, y)] += 1;
		field[calcIndex(x, y + 2)] += 1;
		field[calcIndex(x, y - 2)] += 1;
		field[calcIndex(x + 2, y)] += 1;
	}
}

