import java.io.BufferedReader;
import java.io.InputStreamReader;

public class Main {
	private final static byte MASK_RIGHT = 0x0F; //右4bit数値化用マスク
	private final static int STRIP_LENGTH = 10; //短冊1行の長さ
	private static int[] punchTape = new int[100]; //10*10の方眼紙を短冊にしてくっつけた紙テープ

	public static void main(String[] args) throws Exception {
		BufferedReader reader = new BufferedReader(new InputStreamReader(System.in), 512);
		String sLine;
		while ((sLine = reader.readLine()) != null) {
			droppingInk(sLine.charAt(0) & MASK_RIGHT, sLine.charAt(2) & MASK_RIGHT, sLine.charAt(4) & MASK_RIGHT);
		}
		calcResult();
	}

	/**
	 *都合の良い滴下
	 */
	private static void droppingInk(int x, int y, int size) {
		sinking(x, y - 1);
		sinking(x - 1, y);
		sinking(x, y);
		sinking(x + 1, y);
		sinking(x, y + 1);
		if (size == 1) {
			return;
		}

		sinking(x - 1, y - 1);
		sinking(x + 1, y - 1);
		sinking(x - 1, y + 1);
		sinking(x + 1, y + 1);
		if (size == 2) {
			return;
		}

		sinking(x, y - 2);
		sinking(x - 2, y);
		sinking(x + 2, y);
		sinking(x, y + 2);
	}

	/**
	 * 床は汚さないようにする
	 */
	private static void sinking(int x, int y) {
		if (x >= 0 && x < 10 && y >= 0 && y < 10) {
			punchTape[x * STRIP_LENGTH + y]++;
		}
	}

	/**
	 *紙の染みを数え、損害を報告する
	 */
	private static void calcResult() {
		int white = 0;//綺麗な部分
		int max = 0;//一番ひどい汚れ
		for (int cell : punchTape) {
			if (cell == 0) {
				white++;
			} else if (cell > max) {
				max = cell;
			}
		}
		System.out.println(white);
		System.out.println(max);
	}
}

import java.io.*;
import java.util.*;
import java.util.regex.Pattern;
import java.util.regex.Matcher;

class Main{

	int[][] paper = new int[10][10];

	public void new_paper(){
		for(int x=0;x<10;x++){
			for(int y=0;y<10;y++){
				paper[x][y]=0;
			}
		}
	}

	public void drop_small(int x1,int y1){
		System.out.println(x1 + " " + y1);
		paper[x1][y1]++;
		if(y1-1 >= 0)
			paper[x1][y1-1]++;
		if(x1-1 >= 0)
			paper[x1-1][y1]++;
		if(x1+1 < 10)
			paper[x1+1][y1]++;
		if(y1+1 < 10)
			paper[x1][y1+1]++;
	}

	public void drop_medium(int x1,int y1){
		drop_small(x1,y1);
		if(x1-1 >= 0 && y1-1 >= 0)
			paper[x1-1][y1-1]++;
		if(x1+1 < 10 && y1-1 >= 0)
			paper[x1+1][y1-1]++;
		if(x1-1 >= 0 && y1+1 < 10)
			paper[x1-1][y1+1]++;
		if(x1+1 < 10 && y1+1 < 10)
			paper[x1+1][y1+1]++;
	}

	public void drop_large(int x1,int y1){
		drop_medium(x1,y1);
		if(y1-2 >= 0)
			paper[x1][y1-2]++;
		if(x1-2 >= 0)
			paper[x1-2][y1]++;
		if(x1+2 < 10)
			paper[x1+2][y1]++;
		if(y1+2 < 10)
			paper[x1][y1+2]++;
	}

	public void drop_ink(ArrayList<Integer> list){
		int x1,y1,size;
		new_paper();

		for(int i=0;i<list.size()/3;i++){
			x1 = list.get(i*3);
			y1 = list.get(i*3+1);
			size = list.get(i*3+2);
			if(size == 1)drop_small(x1,y1);
			else if(size == 2)drop_medium(x1,y1);
			else drop_large(x1,y1);
		}
	}

	public void print_paper(){
		int count_zero,max;
		count_zero = 0;
		max = 0;

		for(int x=0;x<10;x++){
			for(int y=0;y<10;y++){
				if(max < paper[x][y])
					max = paper[x][y];
			}
		}

		for(int x=0;x<10;x++){
			for(int y=0;y<10;y++){
				if(paper[x][y] == 0)
					count_zero++;
			}

		}
		System.out.println(count_zero);
		System.out.println(max);
	}

	public static void main(String args[]) throws IOException{
		String str;
		ArrayList <Integer> inp = new ArrayList<Integer>();

		Scanner scan = new Scanner(System.in);

		while(scan.hasNext()){

			str = scan.next();

			String [] strAry = new String[str.length()];
			strAry = str.split(",");
			for(int j=0;j<strAry.length;j++){
				Pattern p = Pattern.compile("[0-9]*");
				Matcher m = p.matcher(strAry[j]);
				if(m.find()){
					int x = Integer.valueOf(m.group(0)).intValue();
					inp.add(x);
				}
			}
		}

		Main object = new Main();
		object.drop_ink(inp);
		object.print_paper();
	}
}

