	public static void dropMin(int x, int y, Map<String, Integer> dropMapData) {

		for (Iterator dropMap = dropMapData.entrySet().iterator(); dropMap.hasNext();) {
			Map.Entry entry = (Map.Entry) dropMap.next();
			String key = (String) entry.getKey();
			refreshMap(x, y, entry, key);

			//十字
			refreshMap(x, y + 1, entry, key);
			refreshMap(x, y - 1, entry, key);
			refreshMap(x + 1, y, entry, key);
			refreshMap(x - 1, y, entry, key);
		}
	}

	//String　の1桁をx 2桁をy
	public static void dropMid(int x, int y, Map<String, Integer> dropMapData) {

		for (Iterator dropMap = dropMapData.entrySet().iterator(); dropMap.hasNext();) {
			Map.Entry entry = (Map.Entry) dropMap.next();
			String key = (String) entry.getKey();
			refreshMap(x, y, entry, key);

			//十字+ななめ
			refreshMap(x, y + 1, entry, key);
			refreshMap(x, y - 1, entry, key);
			refreshMap(x + 1, y, entry, key);
			refreshMap(x - 1, y, entry, key);
			refreshMap(x + 1, y + 1, entry, key);
			refreshMap(x - 1, y - 1, entry, key);
			refreshMap(x + 1, y - 1, entry, key);
			refreshMap(x - 1, y + 1, entry, key);
		}

	}

	//String　の1桁をx 2桁をy
	public static void dropMax(int x, int y, Map<String, Integer> dropMapData) {

		for (Iterator dropMap = dropMapData.entrySet().iterator(); dropMap.hasNext();) {
			Map.Entry entry = (Map.Entry) dropMap.next();
			String key = (String) entry.getKey();
			refreshMap(x, y, entry, key);

			//十字+ななめ
			refreshMap(x, y + 1, entry, key);
			refreshMap(x, y - 1, entry, key);
			refreshMap(x + 1, y, entry, key);
			refreshMap(x - 1, y, entry, key);

			refreshMap(x, y + 2, entry, key);
			refreshMap(x, y - 2, entry, key);
			refreshMap(x + 2, y, entry, key);
			refreshMap(x - 2, y, entry, key);

			refreshMap(x + 1, y + 1, entry, key);
			refreshMap(x - 1, y - 1, entry, key);
			refreshMap(x + 1, y - 1, entry, key);
			refreshMap(x - 1, y + 1, entry, key);
		}

	}

	public static void refreshMap(int x, int y, Map.Entry entry, String key) {

		if (-1 < x && x < 10 && -1 < y && y < 10 && key.equals((String.valueOf(x)).concat(String.valueOf(y)))) {
			entry.setValue((Integer) entry.getValue() + 1);
		}
	}
}

import java.util.*;

public class Main {
	static int[][] paper = new int[14][14];
	static int count , max;

	static void draw ( int x , int y , int paturn ) {
		x += 2;
		y += 2;
		if ( paturn >= 1 ) {
			paper[x][y]++;
			paper[x+1][y]++;
			paper[x][y+1]++;
			paper[x-1][y]++;
			paper[x][y-1]++;
		} if ( paturn >= 2 ) {
			paper[x+1][y+1]++;
			paper[x+1][y-1]++;
			paper[x-1][y+1]++;
			paper[x-1][y-1]++;
		} if ( paturn >= 3 ) {
			paper[x+2][y]++;
			paper[x-2][y]++;
			paper[x][y+2]++;
			paper[x][y-2]++;
		}
		count = 0;
		for ( int i = 2; i < 12; i++ ) {
			for ( int j = 2; j < 12; j++ ) {
				if ( paper[i][j] == 0 ) count++;
				if ( paper[i][j] > max ) max = paper[i][j];
			}
		}
	}

	public static void main(String[] args){
		Scanner sc = new Scanner(System.in).useDelimiter("\\D");
		while ( sc.hasNext() ) {
			try {
				int x = sc.nextInt();
				int y = sc.nextInt();
				int p = sc.nextInt();
				draw(y,x,p);
			} catch ( java.util.InputMismatchException e ) {}
		}
		System.out.println(count+"\n"+max);
	}
}

import java.util.*;

public class Main {

	static int zeroCheck(int[][] p){
		int count=0;
		for(int i[]: p)
			for(int j : i) if(j==0) count++;
		return count;
	}

	static int max(int[][] p){
		int max=0;
		for(int i[] : p)
			for(int j:i) if(max<j) max = j ;
		return max;
	}

	static boolean judge(int x,int y){
		if(x>=0 && x<=9 && y>=0 && y<=9)
			return true;
		else return false;
	}

	static int[][] dropS(int[][] p,int x,int y){
		if(judge(x,y)) p[y][x]++;
		if(judge(x+1,y)) p[y][x+1] ++;
		if(judge(x-1,y)) p[y][x-1] ++;
		if(judge(x,y+1)) p[y+1][x] ++;
		if(judge(x,y-1)) p[y-1][x] ++;
		return p;
	}

	static int[][] dropM(int[][] p,int x,int y){
		for(int i=-1; i<=1; i++){
			for(int j=-1; j<=1; j++){
				if(judge(x+i,y+j)) p[y+j][x+i]++;
			}
		}
		return p;
	}

	static int[][] dropL(int[][] p,int x,int y){
		p = dropM(p,x,y);
		if(judge(x+2,y)) p[y][x+2] ++;
		if(judge(x-2,y)) p[y][x-2] ++;
		if(judge(x,y+2)) p[y+2][x] ++;
		if(judge(x,y-2)) p[y-2][x] ++;
		return p;
	}

	public static String[] kommmaReader(String sc) {
		String line= sc;
		String[] array = line.split(",");
		return array;
	}

	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		int[][] p = new int[10][10];
		do{
			String[] str = kommmaReader(sc.nextLine());
			int x = Integer.parseInt(str[0]);
			int y = Integer.parseInt(str[1]);
			int size = Integer.parseInt(str[2]);

			switch(size){
			case 1: p = dropS(p,x,y); break;
			case 2: p = dropM(p,x,y); break;
			case 3: p = dropL(p,x,y); break;
			}
		} while(sc.hasNext());
		System.out.println(zeroCheck(p));
		System.out.println(max(p));

	}

}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {
	private static final byte MASK_RIGHT = 0x0F;//右4bit数値化用マスク
	private static int[][] paper = new int[10][10];//都合の良い方眼紙

	public static void main(String[] args) throws NumberFormatException, IOException {
		BufferedReader reader = new BufferedReader(new InputStreamReader(System.in), 128);
		String sLine;
		while ((sLine = reader.readLine()) != null) {
			droppingInk(sLine.charAt(0) & MASK_RIGHT, sLine.charAt(2) & MASK_RIGHT, sLine.charAt(4) & MASK_RIGHT);
		}
		calcResult();
	}

	/**
	 *都合の良い滴下
	 */
	private static void droppingInk(int x, int y, int size) {
		sinking(x, y - 1);
		sinking(x - 1, y);
		sinking(x, y);
		sinking(x + 1, y);
		sinking(x, y + 1);
		if (size == 1) {
			return;
		}

		sinking(x - 1, y - 1);
		sinking(x + 1, y - 1);
		sinking(x - 1, y + 1);
		sinking(x + 1, y + 1);
		if (size == 2) {
			return;
		}

		sinking(x, y - 2);
		sinking(x - 2, y);
		sinking(x + 2, y);
		sinking(x, y + 2);
	}

	/**
	 * 床は汚さないようにする
	 */
	private static void sinking(int x, int y) {
		if (x >= 0 && x < 10 && y >= 0 && y < 10) {
			paper[x][y]++;
		}
	}

	/**
	 *紙の染みを数え、損害を報告する
	 */
	private static void calcResult() {
		int white = 0;//綺麗な部分
		int max = 0;//一番ひどい汚れ
		for (int[] row : paper) {
			for (int colum : row) {
				if (colum == 0) {
					white++;
				} else if (colum > max) {
					max = colum;
				}
			}
		}
		System.out.println(white);
		System.out.println(max);
	}
}

import java.io.BufferedReader;
