			Map.Entry entry = (Map.Entry) dropMap.next();
			String key = (String) entry.getKey();
			int value = (Integer) entry.getValue();

			if (maxVal < value) {
				maxVal = value;
			}
			if (value == 0) {
				coutZero++;
			}
		}
		System.out.println(maxVal);
		System.out.println(coutZero);

	}

