						int[][] point_middleInk =
						{{x+1, y-1},{x+1, y+1},{x-1, y+1},{x-1, y-1}};
						point_dropInk(field, point_middleInk);
					case 1:
						int[][] point_smallInk =
						{{x, y-1},{x+1, y},{x, y+1},{x-1, y}};
