		for (int x = 0; x < 10; x++) {
			for (int y = 0; y < 10; y++) {
				dropingMap.put(String.valueOf(x).concat(String.valueOf(y)), 0);

			}
		}
		while (sc.hasNext()) {
			String[] s = sc.next().split(",");
			int dx = Integer.parseInt(s[0]);
			int dy = Integer.parseInt(s[1]);
			int d = Integer.parseInt(s[2]);

			if (d == 1) {

				dropMin(dx, dy, dropingMap);

			} else if (d == 2) {

				dropMid(dx, dy, dropingMap);

			} else {
				dropMax(dx, dy, dropingMap);
			}

			checkMax(dropingMap);
		}

	}

	static void checkMax(Map<String, Integer> dropingMap) {

		int maxVal = 0;
		int coutZero = 0;
		for (Iterator dropMap = dropingMap.entrySet().iterator(); dropMap.hasNext();) {
			Map.Entry entry = (Map.Entry) dropMap.next();
			String key = (String) entry.getKey();
			int value = (Integer) entry.getValue();

			if (maxVal < value) {
				maxVal = value;
			}
			if (value == 0) {
				coutZero = coutZero + 1;
			}
		}
		System.out.println(coutZero);
		System.out.println(maxVal);

	}

	//String　の1桁をx 2桁をy
	static void dropMin(int x, int y, Map<String, Integer> dropMapData) {

		for (Iterator dropMap = dropMapData.entrySet().iterator(); dropMap.hasNext();) {
			Map.Entry entry = (Map.Entry) dropMap.next();
			String key = (String) entry.getKey();
			refreshMap(x, y, entry, key);

			//十字
			refreshMap(x, y + 1, entry, key);
			refreshMap(x, y - 1, entry, key);
			refreshMap(x + 1, y, entry, key);
			refreshMap(x - 1, y, entry, key);
		}
	}

	//String　の1桁をx 2桁をy
	static void dropMid(int x, int y, Map<String, Integer> dropMapData) {

		for (Iterator dropMap = dropMapData.entrySet().iterator(); dropMap.hasNext();) {
			Map.Entry entry = (Map.Entry) dropMap.next();
			String key = (String) entry.getKey();
			refreshMap(x, y, entry, key);

			//十字+ななめ
			refreshMap(x, y + 1, entry, key);
			refreshMap(x, y - 1, entry, key);
			refreshMap(x + 1, y, entry, key);
			refreshMap(x - 1, y, entry, key);
			refreshMap(x + 1, y + 1, entry, key);
			refreshMap(x - 1, y - 1, entry, key);
			refreshMap(x + 1, y - 1, entry, key);
			refreshMap(x - 1, y + 1, entry, key);
		}

	}

	//String　の1桁をx 2桁をy
	static void dropMax(int x, int y, Map<String, Integer> dropMapData) {

		for (Iterator dropMap = dropMapData.entrySet().iterator(); dropMap.hasNext();) {
			Map.Entry entry = (Map.Entry) dropMap.next();
			String key = (String) entry.getKey();
			refreshMap(x, y, entry, key);

			//十字+ななめ
			refreshMap(x, y + 1, entry, key);
			refreshMap(x, y - 1, entry, key);
			refreshMap(x + 1, y, entry, key);
			refreshMap(x - 1, y, entry, key);

			refreshMap(x, y + 2, entry, key);
			refreshMap(x, y - 2, entry, key);
			refreshMap(x + 2, y, entry, key);
			refreshMap(x - 2, y, entry, key);

			refreshMap(x + 1, y + 1, entry, key);
			refreshMap(x - 1, y - 1, entry, key);
			refreshMap(x + 1, y - 1, entry, key);
			refreshMap(x - 1, y + 1, entry, key);
		}

	}

	static void refreshMap(int x, int y, Map.Entry entry, String key) {

		if (-1 < x && x < 10 && -1 < y && y < 10 && key.equals((String.valueOf(x)).concat(String.valueOf(y)))) {
			entry.setValue((Integer) entry.getValue() + 1);
		}
	}
}

import java.io.BufferedReader;
import java.io.InputStreamReader;

public class Main {

	public static void main(String[] args) throws Exception {
		final int X = 0;
		final int Y = 1;
		final int SIZE = 2;
		int countAxis = 0;
		int maxThickness = 0;

		// 真っ白(全てが0である)座標を作成
		int[][] axis = new int[10][10];
		for (int countX = 0; countX < 10; countX++) {
			for (int countY = 0; countY < 10; countY++) {
				axis[countX][countY] = 0;
			}
		}
		BufferedReader input = new BufferedReader(new InputStreamReader(
				System.in));
		// 無限ループ
		while (true) {

			String inputData = input.readLine();
			// 入力データがnullもしくは空文字の時、ループを抜ける
			if (inputData == null || inputData.equals("")) {
				break;
			}
			// 入力データをカンマで区切り配列に格納(0→X軸、１→Y軸、２→インクのサイズ)
			String[] data = inputData.split(",");
			// 入力データのインクのサイズに合わせてそれぞれのメソッドを実行
			if (Integer.parseInt(data[SIZE]) == 1) {
				makeSmallArea(axis, Integer.parseInt(data[X]),
						Integer.parseInt(data[Y]));
			}

			if (Integer.parseInt(data[SIZE]) == 2) {
				makeMiddleArea(axis, Integer.parseInt(data[X]),
						Integer.parseInt(data[Y]));

			}

			if (Integer.parseInt(data[SIZE]) == 3) {
				makeBigArea(axis, Integer.parseInt(data[X]),
						Integer.parseInt(data[Y]));

			}

		}
		// 色がついていない部分(値が0である部分)の数をカウントする
		countAxis = 0;
		for (int countX = 0; countX < 10; countX++) {
			for (int countY = 0; countY < 10; countY++) {
				if (axis[countX][countY] == 0) {
					countAxis++;
				}
				// 一番高い濃度を記録する
				if (axis[countX][countY] > maxThickness) {
					maxThickness = axis[countX][countY];
				}

			}
		}
		// 出力
		System.out.println(countAxis);
		System.out.println(maxThickness);
	}

	// インクサイズが小の場合のメソッド、枠からはみ出した部分を無視するif文を記載
	static void makeSmallArea(int[][] axis, int xAxis, int yAxis) {
		if (xAxis >= 1) {
			axis[xAxis - 1][yAxis]++;
		}
		if (xAxis <= 8) {
			axis[xAxis + 1][yAxis]++;
		}
		if (yAxis >= 1) {
			axis[xAxis][yAxis - 1]++;
		}
		if (yAxis <= 8) {
			axis[xAxis][yAxis + 1]++;
		}
		axis[xAxis][yAxis]++;
	}

	// インクサイズが中の場合のメソッド、枠からはみ出した部分を無視するif文を記載
	static void makeMiddleArea(int[][] axis, int xAxis, int yAxis) {
		makeSmallArea(axis, xAxis, yAxis);
		if (xAxis >= 1 && yAxis >= 1) {
			axis[xAxis - 1][yAxis - 1]++;

		}
		if (xAxis >= 1 && yAxis <= 8) {
			axis[xAxis - 1][yAxis + 1]++;

		}
		if (xAxis <= 8 && yAxis >= 1) {
			axis[xAxis + 1][yAxis - 1]++;
		}
		if (xAxis <= 8 && yAxis <= 8) {
			axis[xAxis + 1][yAxis + 1]++;

		}
	}

	// インクサイズが大の場合のメソッド、枠からはみ出した部分を無視するif文を記載
	static void makeBigArea(int[][] axis, int xAxis, int yAxis) {
		makeMiddleArea(axis, xAxis, yAxis);
		if (xAxis >= 2) {
			axis[xAxis - 2][yAxis]++;
		}
		if (xAxis <= 7) {
			axis[xAxis + 2][yAxis]++;
		}
		if (yAxis >= 2) {
			axis[xAxis][yAxis - 2]++;
		}
		if (yAxis <= 7) {
			axis[xAxis][yAxis + 2]++;
		}
	}
}

public class Main{
  public void run(java.io.InputStream in, java.io.PrintStream out){
    java.util.Scanner sc = new java.util.Scanner(in);
/*answer*/
    int[][] f;
    int i, j, k, x, y, z, t, w, max;
    String str;
    char[] ch;

