							slide[x][y-2] += 1;
							slide[x][y+2] += 1;
						}else if(y == 1){
							slide[x][y+2] += 1;
						}else{
							slide[x][y-2] += 1;
						}
					}
				}
			}else{
				if(y == 0){
					slide[x-1][y] += 1;
					slide[x][y+1] += 1;
					slide[x+1][y] += 1;
					if(size == 2 || size == 3){
						slide[x-1][y+1] += 1;
						slide[x+1][y+1] += 1;
					}
					if(size == 3){
						slide[x][y+2] += 1;
						if(y > 1 && y < 8){
							slide[x-2][y] += 1;
							slide[x+2][y] += 1;
						}else if(y < 2){
							slide[x+2][y] += 1;
						}else{
							slide[x-2][y] += 1;
						}
					}
				}else if(y == 9){
					slide[x-1][y] += 1;
					slide[x][y-1] += 1;
					slide[x+1][y] += 1;
					if(size == 2 || size == 3){
						slide[x-1][y-1] += 1;
						slide[x+1][y-1] += 1;
					}
					if(size == 3){
						slide[x][y-2] += 1;
						if(y > 1 && y < 8){
							slide[x-2][y] += 1;
							slide[x+2][y] += 1;
						}else if(y < 2){
							slide[x+2][y] += 1;
						}else{
							slide[x-2][y] += 1;
						}
					}
				}else{
					slide[x-1][y] += 1;
					slide[x][y-1] += 1;
					slide[x][y+1] += 1;
					slide[x+1][y] += 1;
					if(size == 2 || size == 3){
						slide[x-1][y-1] += 1;
						slide[x-1][y+1] += 1;
						slide[x+1][y-1] += 1;
						slide[x+1][y+1] += 1;
					}
					if(size == 3){
						if(x == 1){
							if(y == 1){
								slide[x+2][y] += 1;
								slide[x][y+2] += 1;
							}else if(y == 8){
								slide[x][y-2] += 1;
								slide[x+2][y] += 1;
							}else{
								slide[x][y-2] += 1;
								slide[x][y+2] += 1;
								slide[x+2][y] += 1;
							}
						}else if(x == 8){
							if(y == 1){
								slide[x-2][y] += 1;
								slide[x][y+2] += 1;
							}else if(y == 8){
								slide[x][y-2] += 1;
								slide[x-2][y] += 1;
							}else{
								slide[x-2][y] += 1;
								slide[x][y-2] += 1;
								slide[x][y+2] += 1;
							}
						}else{
							slide[x-2][y] += 1;
							slide[x+2][y] += 1;
							if(y == 1){
								slide[x][y+2] += 1;
							}else if(y == 8){
								slide[x][y-2] += 1;
							}else{
								slide[x][y+2] += 1;
								slide[x][y-2] += 1;
							}
						}
					}
				}
			}
		}
		int zero = 0;
		int max = 0;
		for(int i = 0;i < 10;i++){
				for(int j = 0;j < 10;j++){
					if(slide[i][j] == 0){
						zero++;
					}
					if(max < slide[i][j]){
						max = slide[i][j];
					}
				}
			}
		System.out.println(zero+"\n"+max);
	}
}

import java.util.HashMap;
import java.util.Map;
import java.util.Scanner;

public class Main {
    /** フィールドサイズ[セル]：X */
    private static final int FIELD_SIZE_X = 10;

    /** フィールドサイズ[セル]：Y */
    private static final int FIELD_SIZE_Y = 10;

    /** 落とすインクとその後のフィールドの状態を保持するMap*/
    private static final Map<Integer, AbstractDropMode> droppedInkMap;

    static {
        // Mapの初期化
        droppedInkMap = new HashMap<Integer, Main.AbstractDropMode>();
        droppedInkMap.put(1, new StarMode(1));
        droppedInkMap.put(2, new SquareMode(1));
        droppedInkMap.put(3, new StarMode(2));
    }

    public static void main(String[] args) {
        try {
            // 場の初期化
            int[][] inkField = new int[FIELD_SIZE_X - 1][FIELD_SIZE_Y - 1];

            // input ループのためにScanner
            Scanner sc = new Scanner(System.in);

            // 入力に応じてインクを落とす
            while (sc.hasNext()) {
                String[] input = sc.next().split(",");
                int x = Integer.valueOf(input[0]);
                int y = Integer.valueOf(input[1]);
                int size = Integer.valueOf(input[2]);

                droppedInkMap.get(size).drop(inkField, x, y);
            }

            // 結果の主計
            System.out.println(countWhiteCells(inkField));
            System.out.println(getMostDroppedCount(inkField));

        } catch (Exception e) {
            e.printStackTrace();
            System.exit(0);
        }

    }

    private static int countWhiteCells(int[][] inkField) {
        int result = 0;
        for (int[] xField : inkField) {
            for (int cell : xField) {
                result = cell == 0 ? result + 1 : result;
            }
        }
        return result;
    }

    private static int getMostDroppedCount(int[][] inkField) {
        int result = 0;
        for (int[] xField : inkField) {
            for (int cell : xField) {
                result = cell > result ? cell : result;
            }
        }
        return result;
    }

    private abstract static class AbstractDropMode {
        protected int size;

        AbstractDropMode(int size) {
            this.size = size;
        }

        public abstract void drop(int[][] inkField, int dropPointX,
                int dropPointY);

        protected boolean isOverField(int point, int diff, int lowerLimit,
                int upperLimit) {
            return((point + diff) < lowerLimit || (point + diff) >= upperLimit);
        }
    }

    /** 四角形にインクを落とす<br> x = ±size, y = ±sizeの範囲にインクが落ちる */
    private static class SquareMode extends AbstractDropMode {
        SquareMode(int size) {
            super(size);
        }

        @Override
        public void drop(int[][] inkField, int dropPointX, int dropPointY) {
            for (int x = -1 * this.size; x <= this.size; x++) {
                // フィールド外なら処理しない
                if (isOverField(dropPointX, x, 0, inkField.length)) {
                    continue;
                }

                for (int y = -1 * this.size; y <= this.size; y++) {
                    if (isOverField(dropPointY, y, 0,
                            inkField[dropPointX + x].length)) {
                        continue;
                    }

                    inkField[dropPointX + x][dropPointY + y]++;
                }
            }
        }
    }

    /** 星形にインクを落とす<br> x + y = ±sizeの範囲にインクが落ちる */
    private static class StarMode extends AbstractDropMode {
        StarMode(int size) {
            super(size);
        }

        @Override
        public void drop(int[][] inkField, int dropPointX, int dropPointY) {
            for (int x = -1 * this.size; x <= this.size; x++) {
                // フィールド外なら処理しない
                if (isOverField(dropPointX, x, 0, inkField.length)) {
                    continue;
                }

                for (int y = -1 * (this.size - Math.abs(x)); y <= (this.size - Math
                        .abs(x)); y++) {

                    if (isOverField(dropPointY, y, 0,
                            inkField[dropPointX + x].length)) {
                        continue;
                    }

                    inkField[dropPointX + x][dropPointY + y]++;
                }
            }
        }
    }

}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {

	public static void main(String[] args) {
		int field[][] = setField();
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		String input = null;
		while (true) {
			try {
				input = br.readLine();
			} catch (IOException e) {
				e.printStackTrace();
			}
			if (input == null || input.isEmpty()) {
				break;
			}
			;
			String[] inputs = input.split(",");
			drop(field, Integer.parseInt(inputs[0]),
					Integer.parseInt(inputs[1]),
					Integer.parseInt(inputs[2]));
		}
		amountOfWhite(field);
		maxDepth(field);

	}

	private static int[][] setField() {
		int field[][] = new int[10][10];
		for (int i = 0; i < 10; i++) {
			for (int j = 0; j < 10; j++) {
				field[i][j] = 0;
			}
		}
		return field;
	}

	private static void drop(int[][] field, int x, int y, int size) {
		switch (size) {
		case 1:
			dropSmall(field, x, y);
			break;

		case 2:
			dropMiddle(field, x, y);
			break;
		case 3:
			dropLarge(field, x, y);
			break;
		}

	}

	private static void dropLarge(int[][] field, int x, int y) {

		dropMiddle(field, x, y);

		int dropX[] = { -2, 0, 2, 0 };
		int dropY[] = { 0, -2, 0, 2 };

		for (int i = 0; i < dropX.length; i++) {
			dropping(field, x + dropX[i], y + dropY[i]);
		}
	}

	private static void dropMiddle(int[][] field, int x, int y) {

		dropSmall(field, x, y);

		int dropX[] = { -1, -1, 1, 1 };
		int dropY[] = { 1, -1, 1, -1 };

		for (int i = 0; i < dropX.length; i++) {
			dropping(field, x + dropX[i], y + dropY[i]);
		}
	}

	private static void dropSmall(int[][] field, int x, int y) {
		int dropX[] = { -1, 0, 1, 0 };
		int dropY[] = { 0, -1, 0, 1 };
		dropping(field, x, y);

		for (int i = 0; i < dropX.length; i++) {
			dropping(field, x + dropX[i], y + dropY[i]);
		}
	}

	private static int[][] dropping(int[][] field, int x, int y) {
		if (0 <= x || x < 10 || 0 <= y || y < 10) {
			field[x][y] += 1;
		}
		return field;
	}

	private static void amountOfWhite(int[][] field) {
		int count = 0;
		for (int i = 0; i < 10; i++) {
			for (int j = 0; j < 10; j++) {
				if (field[i][j] == 0) {
					count++;
				}
			}
		}
		System.out.println(count);
	}

	private static void maxDepth(int[][] field) {
		int maxDepth = 0;
		for (int i = 0; i < 10; i++) {
			for (int j = 0; j < 10; j++) {
				if (maxDepth < field[i][j]) {
					maxDepth = field[i][j];
				}
			}
		}
		System.out.println(maxDepth);
	}
}

import java.io.IOException;
import java.util.Scanner;
import java.util.StringTokenizer;

public class Main {

	private static Scanner sc;
	private static int[][] squares;

	static {
		sc = new Scanner(System.in);
		squares = new int[14][14];
	}

	public static void main(String[] args) throws IOException {

		String line;
		int x, y, size, blind = 0, max = 0;

		while ((line = sc.nextLine()) != null && !line.isEmpty()) {
			StringTokenizer st = new StringTokenizer(line, ",");
			x = Integer.parseInt(st.nextToken());
			y = Integer.parseInt(st.nextToken());
			size = Integer.parseInt(st.nextToken());
			set(x, y, size);
		}
		for (x = 2; x < 12; x++) {
			for (y = 2; y < 12; y++) {
				if (squares[x][y] == 0) {
					blind++;
				}
				if (max < squares[x][y]) {
					max = squares[x][y];
				}
			}
		}
		System.out.println(blind + "\n" + max);
	}

	private static void set(int x, int y, int size) {
		squares[x][y]++;
		squares[x - 1][y]++;
		squares[x + 1][y]++;
		squares[x][y - 1]++;
		squares[x][y + 1]++;
		if (size >= 2) {
			squares[x - 1][y - 1]++;
			squares[x - 1][y + 1]++;
			squares[x + 1][y - 1]++;
			squares[x + 1][y + 1]++;
		}
		if (size >= 3) {
			squares[x - 2][y]++;
			squares[x + 2][y]++;
			squares[x][y - 2]++;
			squares[x][y + 2]++;
		}
	}
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.ArrayList;
import java.util.List;
import java.util.StringTokenizer;

public class Main {

	// フィールド
	static int droppingField[][] = new int[10][10];
	// インクが落ちていない数
	static int clearCount = 0;
	// 一番濃いインク
	static int maxDoropping = 0;

	public static void main(String[] args) throws NumberFormatException,
			IOException {

		// 標準入力よりインプットを取得
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		List<int[]> inkList = new ArrayList<int[]>();

		// 最終行まで読み込む
		String line = "";
		while ((line = br.readLine()) != null) {

			// 1行をデータの要素に分割
			StringTokenizer st = new StringTokenizer(line, ",");

			// リストに格納
			while (st.hasMoreTokens()) {
				int[] input = { Integer.parseInt(st.nextToken()),
						Integer.parseInt(st.nextToken()),
						Integer.parseInt(st.nextToken()) };
				inkList.add(input);
			}
		}
		br.close();

		// インクを塗る
		drop(inkList, droppingField);

		// インクが落ちていない数、 一番濃いインクを求める
		outputResult();

		// インクが落ちていない数、 一番濃いインクを出力する
		System.out.println(clearCount);
		System.out.println(maxDoropping);

	}

	// 引数にインクとフィールドを指定し、フィールドにインクを落とす
	public static void drop(List<int[]> inkList, int[][] droppingField) {

		List<int[]> inks = new ArrayList<int[]>();
		int maxLength = droppingField.length;

		// リストをばらしてドロップできる状態に
		for (int[] a : inkList) {

			if (a[2] == 1) {
				inks.addAll(smallInk(a[0], a[1], maxLength));
			} else if (a[2] == 2) {
				inks.addAll(middiumInk(a[0], a[1], maxLength));
			} else if (a[2] == 3) {
				inks.addAll(largeInk(a[0], a[1], maxLength));
			}

		}
		droppingInk(inks);

	}

	// 小さいの塗る
	public static List<int[]> smallInk(int x, int y, int maxLength) {
		List<int[]> inks = new ArrayList<int[]>();

		int[] ink1 = { x, y };
		int[] ink2 = { x - 1, y };
		int[] ink3 = { x + 1, y };
		int[] ink4 = { x, y - 1 };
		int[] ink5 = { x, y + 1 };

		inks.add(ink1);
		inks.add(ink2);
		inks.add(ink3);
		inks.add(ink4);
		inks.add(ink5);

		return inks;
	}

	// 中くらいの塗る
	public static List<int[]> middiumInk(int x, int y, int maxLength) {
		List<int[]> inks = new ArrayList<int[]>();

		for (int i = x - 1; i <= x + 1; i++) {
			for (int n = y - 1; n <= y + 1; n++) {
				int[] ink1 = { i, n };
				inks.add(ink1);
			}
		}

		return inks;

	}

	// 大きいの塗る
	public static List<int[]> largeInk(int x, int y, int maxLength) {
		List<int[]> inks = new ArrayList<int[]>();

		for (int i = x - 1; i <= x + 1; i++) {
			for (int n = y - 1; n <= y + 1; n++) {
				int[] ink1 = { i, n };
				inks.add(ink1);
			}
		}
		int[] ink2 = { x - 2, y };
		int[] ink3 = { x + 2, y };
		int[] ink4 = { x, y - 2 };
		int[] ink5 = { x, y + 2 };

		inks.add(ink2);
		inks.add(ink3);
		inks.add(ink4);
		inks.add(ink5);

		return inks;
	}

	// インクをフィールドに塗る
	public static void droppingInk(List<int[]> inks) {
		int maxLength = droppingField.length;

		for (int[] a : inks) {
			if (a[0] >= 0 && a[0] < maxLength && a[1] >= 0 && a[1] < maxLength) {
				droppingField[a[0]][a[1]]++;
			}
		}

	}

	// インクが落ちていない数、 一番濃いインクを算出する
	public static void outputResult() {

		for (int i = 0; i < droppingField.length; i++) {
			for (int n = 0; n < droppingField[0].length; n++) {

				if (droppingField[i][n] == 0) {
					// 塗られていない箇所(0)を数える
					clearCount++;
				} else {
					// 一番濃いインクを保持する
					maxDoropping = Math.max(maxDoropping, droppingField[i][n]);
				}
			}
		}

	}

}

import java.util.*;

public class Main {
