		while(true){
			try{
				String str[] = sc.nextLine().split(",");
				int x = Integer.parseInt(str[0]);
				int y = Integer.parseInt(str[1]);
				int ink = Integer.parseInt(str[2]);
				if(ink >= 1){
					if(y > 0){
						yx[y-1][x]++;
