            int size=Integer.parseInt(s[2]);
            if(size==1){
                small(x,y);
            }else if(size==2){
                medium(x,y);
            }else{
                large(x,y);
            }
        }
        int ans1=0,ans2=0;
        for(int i=0;i<10;i++){
            for(int j=0;j<10;j++){
                if(map[i][j]==0){
                    ans1++;
                }
                if(map[i][j]>ans2){
                    ans2=map[i][j];
                }
            }
        }
        System.out.println(ans1);
        System.out.println(ans2);
