				for(int i = -1; i <= 1; i++) {
					if(n[0] + i < 0) continue;
					for(int j = -1; j <= 1; j++) {
						if(n[1] + j < 0) continue;
						board[n[0] + i][n[1] + j]++;
					}
				}
