		int[][] map = new int[14][14];
		int count = 0,max = 0;

		while(sc.hasNext()){

			int[] x = new int[3];

			String[] code = sc.next().split(",",0);

			for(int i = 0; i < code.length; i++)
				x[i] = Integer.parseInt(code[i]);

			x[0] += 2;
			x[1] += 2;

			if(x[2] >= 1){
				map[x[0]][x[1]]++;
				map[x[0]][x[1]-1]++;
				map[x[0]][x[1]+1]++;
				map[x[0]-1][x[1]]++;
				map[x[0]+1][x[1]]++;
				}
			if(x[2] >= 2){
				map[x[0]-1][x[1]-1]++;
				map[x[0]+1][x[1]-1]++;
				map[x[0]-1][x[1]+1]++;
				map[x[0]+1][x[1]+1]++;
			}
			if(x[2] == 3){
				map[x[0]][x[1]-2]++;
