		// 宣言
		Scanner sc = new Scanner(System.in);
		String[] strarray;

		while (sc.hasNext()) {
			strarray = sc.nextLine().split(",");
			int x = Integer.parseInt(strarray[0]);
			int y = Integer.parseInt(strarray[1]);
			int size = Integer.parseInt(strarray[2]);
			if (size == 1) {
				small(x, y);
			} else if (size == 2) {
				middle(x, y);
			} else if (size == 3) {
				big(x, y);
			}
		}

		int count = 0;
		int max = 0;

		for (int xx = 0; xx < 10; xx++) {
			for (int yy = 0; yy < 10; yy++) {
				// 1個目の答え
				if (board[xx][yy] == 0) {
					count++;
				}
				// 2個目の答え
				if (max < board[xx][yy]) {
					max = board[xx][yy];
				}
			}
		}

		System.out.println(count);
		System.out.println(max);

	}

	static void small(int x, int y) {
		int smallx[] = { 1, 0, -1, 0 };
		int smally[] = { 0, 1, 0, -1 };
		paint(x, y);
		for (int i = 0; i < 4; i++) {
			int nx = smallx[i] + x;
			int ny = smally[i] + y;
			paint(nx, ny);
		}
	}

	static void middle(int x, int y) {
		for (int yrange = -1; yrange <= 1; yrange++) {
			for (int xrange = -1; xrange <= 1; xrange++) {
				paint(x + xrange, y + yrange);
			}
		}
	}

	static void big(int x, int y) {
		middle(x, y);
		paint(x, y - 2);
		paint(x, y + 2);
		paint(x + 2, y);
		paint(x - 2, y);
	}

	static void paint(int x, int y) {
		if (x < 0 || y < 0 || x >= 8 || y >= 8) {
			return;
		} else {
			board[x][y] += 1;
		}
	}

}

import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scan = new Scanner(System.in).useDelimiter("\\n|,");
        int[] area = new int[10*10];

        while (scan.hasNextInt()) {
            int x = scan.nextInt();
            int y = scan.nextInt();
            int size = scan.nextInt();

            int point = y * 10 + x;

            switch (size) {
            case 3:
                if(x < 8) area[point + 2]++;
                if(x > 1) area[point - 2]++;
                if(y < 8) area[point + 20]++;
                if(y > 1) area[point - 20]++;
            case 2:
                //左上
                if(x > 0 && y > 0) area[point - 11]++;
                //右上
                if(x < 9 && y > 0) area[point - 9]++;
                //左下
                if(y < 9 && x > 0) area[point + 9]++;
                //右下
                if(y < 9 && x < 9) area[point + 11]++;
            case 1:
                if(x < 9) area[point + 1]++;
                if(x > 0) area[point - 1]++;
                if(y < 9) area[point + 10]++;
                if(y > 1) area[point - 10]++;
                area[point]++;
            }

        }

        Arrays.sort(area);
        int blank = 0;
        for(int i = 0; i < 100; i++ ){
            if(area[i] != 0) {
                blank = i;
                break;
            }
        }
        //System.out.println(Arrays.binarySearch(area, 1));
        System.out.println(blank);
        System.out.println(area[99]);

    }

}

