import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;


public class Main {
	static int[][] grid = new int[14][14];

	static void dropL(int x, int y) {
		grid[x][y-2]++;
		grid[x-1][y-1]++;grid[x][y-1]++;grid[x+1][y-1]++;
		grid[x-2][y]++;grid[x-1][y]++;grid[x][y]++;grid[x+1][y]++;grid[x+2][y]++;
		grid[x-1][y+1]++;grid[x][y+1]++;grid[x+1][y+1]++;
		grid[x][y+2]++;
	}

	static void dropM(int x, int y) {
		grid[x-1][y-1]++;grid[x][y-1]++;grid[x+1][y-1]++;
		grid[x-1][y]++;grid[x][y]++;grid[x+1][y]++;
		grid[x-1][y+1]++;grid[x][y+1]++;grid[x+1][y+1]++;
	}

	static void dropS(int x, int y) {
		grid[x][y-1]++;
		grid[x-1][y]++;grid[x][y]++;grid[x+1][y]++;
		grid[x][y+1]++;
	}

	public static void main(String[] args) throws IOException {
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));

		String line;
		while ((line = br.readLine()) != null) {
			String[] terms = line.split(",");
			int x = Integer.parseInt(terms[0]);
			int y = Integer.parseInt(terms[1]);
			int s = Integer.parseInt(terms[2]);

			switch (s) {
			case 1: dropS(x, y); break;
			case 2: dropM(x, y); break;
			case 3: dropL(x, y); break;
			}
		}

		int W = 100, T = 0;
		for (int x = 2 ; x < 12 ; x++) {
			for (int y = 2 ; y < 12 ; y++) {
				if (grid[x][y] > 0) {
					W--;
					T = Math.max(T, grid[x][y]);
				}
			}
		}
		System.out.println(W+"\n"+T);
	}
}

import java.util.Scanner;


public class Main {
	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);

		int x = 0, y, size, max = 0, cnt = 0;
		int[][] paper = new int[10][10];
		String[] strings;
		for(int i = 0; i < 10; i++){
			for(int j = 0; j < 10; j++){
				paper[i][j] = 0;
			}
		}

		while(sc.hasNext()){
			strings = sc.next().split(",");
			x = Integer.parseInt(strings[0]);
			y = Integer.parseInt(strings[1]);
			size = Integer.parseInt(strings[2]);

			if(size == 1){
				if(y - 1 >= 0) paper[x][y - 1]++;
				for(int i = 0; i < 3; i++){
					if(x - 1 + i >= 0 && x - 1 + i < 10) paper[x - 1 + i][y]++;
				}
				if(y + 1 >= 0) paper[x][y + 1]++;
			}else if(size == 2){
				for(int i = 0; i < 3; i++){
					for(int j = 0; j < 3; j++){
						if((x - 1 + j >= 0  && x - 1 + j < 10) && (y - 1 + i >= 0 && y - 1 + i < 10)) paper[x - 1 + j][y - 1 + i]++;
					}
				}
			}else{
				for(int i = 0; i < 3; i++){
					for(int j = 0; j < 3; j++){
						if((x - 1 + j >= 0  && x - 1 + j < 10) && (y - 1 + i >= 0 && y - 1 + i < 10)) paper[x - 1 + j][y - 1 + i]++;
					}
				}
				if(y - 2 >= 0) paper[x][y - 2]++;
				if(x - 2 >= 0) paper[x - 2][y]++;
				if(x + 2 >= 0) paper[x + 2][y]++;
				if(y + 2 >= 0) paper[x][y + 2]++;
			}
		}

		for(int i = 0; i < 10; i++){
			for(int j = 0; j < 10; j++){
				if(paper[j][i] == 0){
					cnt++;
				}
				else if(paper[j][i] > max){
					max = paper[j][i];
				}
			}
		}
		System.out.println(cnt);
		System.out.println(max);
	}
}

