				if(judge(x, y)){
					Map[x][y]++;
				}
				if(judge(x, y-1)){
					Map[x][y-1]++;
				}
				if(judge(x-1, y)){
					Map[x-1][y]++;
				}
				if(judge(x+1, y)){
					Map[x+1][y]++;
				}
				if(judge(x, y+1)){
					Map[x][y+1]++;
				}
			}
			if(scale >= 2){
				if(judge(x-1, y-1)){
					Map[x-1][y-1]++;
				}
				if(judge(x+1, y+1)){
					Map[x+1][y+1]++;
				}
				if(judge(x+1, y-1)){
					Map[x+1][y+1]++;
				}
				if(judge(x-1, y+1)){
					Map[x+1][y+1]++;
				}
			}
			if(scale >= 3){
				if(judge(x, y-2)){
					Map[x][y-2]++;
				}
				if(judge(x-2, y)){
					Map[x-2][y]++;
				}
				if(judge(x+2, y)){
					Map[x+2][y]++;
				}
				if(judge(x, y+2)){
					Map[x][y+2]++;
				}
			}

