    private static int maxValue = 0;

    /**
     * @param args
     * @throws IOException
     */
    public static void main(String[] args) throws IOException {
        // 入力読取り
        InputStreamReader streamReader = new InputStreamReader(System.in);
        BufferedReader bufferedReader = new BufferedReader(streamReader);

        String line = null;

        while ((line = bufferedReader.readLine()) != null) {
            String[] nums = line.split(",");

            int x = Integer.parseInt(nums[0]) + 2;
            int y = Integer.parseInt(nums[1]) + 2;
            int size = Integer.parseInt(nums[2]);

            // インクのサイズで分類してマス目の値を増加
            if (size == 1) {
                sinkSmall(x, y);

            } else if (size == 2) {
                sinkMiddle(x, y);

            } else if (size == 3) {
                sinkLarge(x, y);

            }
        }

        // 色のついていないマス目の個数と一番色の濃いマス目の値を求める
        for (int i = 2; i < 12; ++i) {
            for (int j = 2; j < 12; ++j) {
                if (cells[i][j] == 0) {
                    ++count;
                }
                if (maxValue <= cells[i][j]) {
                    maxValue = cells[i][j];
                }
            }
        }

        System.out.println(count);
        System.out.println(maxValue);

    }

    /**
     * @param x
     * @param y
     */
    private static void sinkLarge(int x, int y) {
        sinkMiddle(x, y);
        ++cells[y][x - 2];
        ++cells[y][x + 2];
        ++cells[y - 2][x];
        ++cells[y + 2][x];
    }

    /**
     * @param x
     * @param y
     */
    private static void sinkMiddle(int x, int y) {
        sinkSmall(x, y);
        ++cells[y - 1][x - 1];
        ++cells[y - 1][x + 1];
        ++cells[y + 1][x - 1];
        ++cells[y + 1][x + 1];
    }

    /**
     * @param x
     * @param y
     */
    private static void sinkSmall(int x, int y) {
        ++cells[y][x];
        ++cells[y - 1][x];
        ++cells[y + 1][x];
        ++cells[y][x - 1];
        ++cells[y][x + 1];
    }

}

import java.util.Scanner;

public class Main {
 static int     max   = 0;
 static int     count = 10 * 10;
 static int[][] p     = new int[10][10];

 public static void main(String[] args) {
	Scanner in = new Scanner(System.in)
	  .useDelimiter("[^0-9]+");
	while (in.hasNextLine()) {
	 ink(in.nextInt(), in.nextInt(), in.nextInt());
	}
	System.out.println(count);
	System.out.println(max);

 }

 static void ink(int x, int y, final int size) {
	switch (size) {
	case 3:
	 pi(x + 2, y);
	 pi(x - 2, y);
	 pi(x, y + 2);
	 pi(x, y - 2);
	case 2:
	 pi(x + 1, y + 1);
	 pi(x + 1, y - 1);
	 pi(x - 1, y + 1);
	 pi(x - 1, y - 1);
	case 1:
	 pi(x + 1, y);
	 pi(x - 1, y);
	 pi(x, y + 1);
	 pi(x, y - 1);
	default:
	 pi(x, y);
	}

 }

 static void pi(int x, int y) {
	if ((0 <= x && x < 10) && (0 <= y && y < 10)) {
	 if (p[x][y] == 0) count--;
	 p[x][y]++;
	 max = Math.max(max, p[x][y]);
	}
 }
}

package aizuonlinejudge;

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.StringTokenizer;

public class Main {

	private static BufferedReader br;
	private static int[][] squares;

	static {
		br = new BufferedReader(new InputStreamReader(System.in));
		squares = new int[10][10];
	}

	public static void main(String[] args) throws IOException {

		String line;
		int x, y, size, blind = 100, max = 0;

		while ((line = br.readLine()) != null) {
			StringTokenizer st = new StringTokenizer(line, ",");

			while (st.hasMoreTokens()) {
				x = Integer.parseInt(st.nextToken());
				y = Integer.parseInt(st.nextToken());
				size = Integer.parseInt(st.nextToken());

				squares[x][y]++;
				switch (size) {
				case 1:
					set1(x, y);
					break;
				case 2:
					set1(x, y);
					set2(x, y);
					break;
				case 3:
					set1(x, y);
					set2(x, y);
					set3(x, y);
				default:
					break;
				}
			}
		}
		for (int[] square : squares) {
			for (int value : square) {
				if (value != 0) {
					blind--;
				}
				if (max < value) {
					max = value;
				}
			}
		}
		System.out.println(blind + "\n" + max);
	}

	private static void set1(int x, int y) {
		if (x > 0) {
			squares[x - 1][y]++;
		}
		if (x < 9) {
			squares[x + 1][y]++;
		}
		if (y > 0) {
			squares[x][y - 1]++;
		}
		if (y < 9) {
			squares[x][y + 1]++;
		}
	}

	private static void set2(int x, int y) {
		if (x > 0 && y > 0) {
			squares[x - 1][y - 1]++;
		}
		if (x > 0 && y < 9) {
			squares[x - 1][y + 1]++;
		}
		if (x < 9 && y > 0) {
			squares[x + 1][y - 1]++;
		}
		if (x < 9 && y < 10) {
			squares[x + 1][y + 1]++;
		}
	}

	private static void set3(int x, int y) {
		if (x - 1 > 0) {
			squares[x - 2][y]++;
		}
		if (x + 1 < 9) {
			squares[x + 2][y]++;
		}
		if (y - 1 > 0) {
			squares[x][y - 2]++;
		}
		if (y + 1 < 9) {
			squares[x][y + 2]++;
		}
	}
}

