import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

/**
 * [機 能] マス目の紙にインクを落とし、色のついてないマスの数と最も濃いマス目の濃さを出力する機能
 * [説 明]
 * [備 考]
 */
public class Main {

	static final int MAX_LENGTH = 10;
	static final int MAX_WIDTH = 10;
	static final int SMALL_INK = 1;
	static final int MIDDLE_INK = 2;
	static final int LARGE_INK = 3;

	/**
	 * [機 能] マス目の紙にインクを落とし、色のついてないマスの数と最も濃いマス目の濃さを出力する機能
	 * [説 明]
	 * [備 考]
	 */
	public static void main(String[] args) {
		int field[][] = setField();
		//入力値を読み込む
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		String input = null;
		while (true) {
			try {
				input = br.readLine();
			} catch (IOException e) {
				e.printStackTrace();
			}
			if (input == null || input.isEmpty()) {
				break;
			}
			String[] inputs = input.split(",");
			//インクを落とす
			dropInk(field, Integer.parseInt(inputs[0]),
					Integer.parseInt(inputs[1]),
					Integer.parseInt(inputs[2]));
		}
		//色のついてないマスの数と最も濃いマス目の濃さを出力
		amountOfWhite(field);
		maxDepth(field);
	}

	/**
	 * [機 能] マス目の紙を用意する
	 * [説 明]
	 * [備 考] 今回マス目の紙をフィールドと呼ぶことにする
	 */
	private static int[][] setField() {
		int field[][] = new int[MAX_LENGTH][MAX_WIDTH];
		for (int i = 0; i < MAX_LENGTH; i++) {
			for (int j = 0; j < MAX_WIDTH; j++) {
				field[i][j] = 0;
			}
		}
		return field;
	}

	/**
	 * [機 能] 紙にインクを落とす機能
	 * [説 明]
	 * [備 考]
	 * @param field マス目の紙
	 * @param x 紙の縦軸
	 * @param y 紙の横軸
	 * @param size インクのサイズ
	 */
	private static void dropInk(int[][] field, int x, int y, int size) {
		switch (size) {
		case SMALL_INK:
			dropSmall(field, x, y);
			break;
		case MIDDLE_INK:
			dropMiddle(field, x, y);
			break;
		case LARGE_INK:
			dropLarge(field, x, y);
			break;
		}
	}

	/**
	 * [機 能] 「大」サイズのインクを落とす機能
	 * [説 明]
	 * [備 考]
	 * @param field マス目の紙
	 * @param x 紙の縦軸
	 * @param y 紙の横軸
	 */
	private static void dropLarge(int[][] field, int x, int y) {

		dropMiddle(field, x, y);

		int dropX[] = { -2, 0, 2, 0 };
		int dropY[] = { 0, -2, 0, 2 };

		for (int i = 0; i < dropX.length; i++) {
			dropping(field, x + dropX[i], y + dropY[i]);
		}
	}

	/**
	 * [機 能] 「中」サイズのインクを落とす機能
	 * [説 明]
	 * [備 考]
	 * @param field マス目の紙
	 * @param x 紙の縦軸
	 * @param y 紙の横軸
	 */
	private static void dropMiddle(int[][] field, int x, int y) {

		dropSmall(field, x, y);

		int dropX[] = { -1, -1, 1, 1 };
		int dropY[] = { 1, -1, 1, -1 };

		for (int i = 0; i < dropX.length; i++) {
			dropping(field, x + dropX[i], y + dropY[i]);
		}
	}

	/**
	 * [機 能] 「小」サイズのインクを落とす機能
	 * [説 明]
	 * [備 考]
	 * @param field マス目の紙
	 * @param x 紙の縦軸
	 * @param y 紙の横軸
	 */
	private static void dropSmall(int[][] field, int x, int y) {
		int dropX[] = { -1, 0, 1, 0 };
		int dropY[] = { 0, -1, 0, 1 };
		dropping(field, x, y);

		for (int i = 0; i < dropX.length; i++) {
			dropping(field, x + dropX[i], y + dropY[i]);
		}
	}

	/**
	 * [機 能] インクのしみこむ範囲が紙の外にはみ出ないか判定し、外であればしみこませない機能
	 * [説 明]
	 * [備 考]
	 * @param field マス目の紙
	 * @param x 紙の縦軸
	 * @param y 紙の横軸
	 * @return field マス目の紙
	 */
	private static int[][] dropping(int[][] field, int x, int y) {
		if (0 <= x && x < 10 && 0 <= y && y < 10) {
			field[x][y] += 1;
		}
		return field;
	}

	/**
	 * [機 能] 色のついてないマスの数を出力する機能
	 * [説 明]
	 * [備 考]
	 * @param field マス目の紙
	 */
	private static void amountOfWhite(int[][] field) {
		int count = 0;
		for (int i = 0; i < MAX_LENGTH; i++) {
			for (int j = 0; j < MAX_WIDTH; j++) {
				if (field[i][j] == 0) {
					count++;
				}
			}
		}
		System.out.println(count);
	}

	/**
	 * [機 能] 最も濃いマス目の濃さを出力する機能
	 * [説 明]
	 * [備 考]
	 * @param field マス目の紙
	 */
	private static void maxDepth(int[][] field) {
		int maxDepth = 0;
		for (int i = 0; i < MAX_LENGTH; i++) {
			for (int j = 0; j < MAX_WIDTH; j++) {
				if (maxDepth < field[i][j]) {
					maxDepth = field[i][j];
				}
			}
		}
		System.out.println(maxDepth);
	}
}

import java.util.*;

public class Main {

	static int zeroCheck(int[][] p){
		int count=0;
		for(int i[]: p)
			for(int j : i) if(j==0) count++;
		return count;
	}

	static int max(int[][] p){
		int max=0;
		for(int i[] : p)
			for(int j:i) if(max<j) max = j ;
		return max;
	}

	static boolean judge(int x,int y){
		if(x>=0 && x<=9 && y>=0 && y<=9)
			return true;
		else return false;
	}

	static int[][] dropS(int[][] p,int x,int y){
		if(judge(x,y)) p[y][x]++;
		if(judge(x+1,y)) p[y][x+1] ++;
		if(judge(x-1,y)) p[y][x-1] ++;
		if(judge(x,y+1)) p[y+1][x] ++;
		if(judge(x,y-1)) p[y-1][x] ++;
		return p;
	}

	static int[][] dropM(int[][] p,int x,int y){
		for(int i=-1; i<=1; i++){
			for(int j=-1; j<=1; j++){
				if(judge(x+i,y+j)) p[y+j][x+i]++;
			}
		}
		return p;
	}

	static int[][] dropL(int[][] p,int x,int y){
		p = dropM(p,x,y);
		if(judge(x+2,y)) p[y][x+2] ++;
		if(judge(x-2,y)) p[y][x-2] ++;
		if(judge(x,y+2)) p[y+2][x] ++;
		if(judge(x,y-2)) p[y-2][x] ++;
		return p;
	}

	public static String[] kommmaReader() {
		Scanner sc = new Scanner(System.in);
		String line;
		line=sc.nextLine();
		String[] array = line.split(",");
		return array;
	}

	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		int[][] p = new int[10][10];
		while(sc.hasNextLine()){
			String[] str = kommmaReader();
			int x = Integer.parseInt(str[0]);
			int y = Integer.parseInt(str[1]);
			int size = Integer.parseInt(str[2]);

			switch(size){
			case 1: p = dropS(p,x,y); break;
			case 2: p = dropM(p,x,y); break;
			case 3: p = dropL(p,x,y); break;
			}
		}
		System.out.println(zeroCheck(p));
		System.out.println(max(p));
	}

}

import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

public class Main{

    public static void main(String[] args) throws Exception {

        exec();
    }

    private static void exec() throws Exception {

        InputStreamReader isr = new InputStreamReader(System.in);
        BufferedReader br = new BufferedReader(isr);
        String line;

        List<String> mapPointList = new ArrayList<String>();
        int[][] dropPoint = null; // インキが滲んだ座標

        while ((line = br.readLine()) != null) {

            String[] input = line.split(",");

            int x = Integer.parseInt(input[0]); // x座標
            int y = Integer.parseInt(input[1]); // y座標
            int size = Integer.parseInt(input[2]); // インキ滴サイズ

            // サイズ毎にインキが滲む座標を計算
            if (size == 1) {
                dropPoint = smallDrop(x, y);
            } else if (size == 2) {
                dropPoint = middleDrop(x, y);
            } else if (size == 3) {
                dropPoint = largeDrop(x, y);
            }

            // インキが滲んだ紙の座標を計算
            mapPointList = writePointMapArray(mapPointList, dropPoint);
        }

        // 結果を計算
        int[] result = writeDeepPoint(mapPointList);

        System.out.println(result[0]);
        System.out.println(result[1]);
    }

    private static List<String> writePointMapArray(List<String> mapPointList, int[][] pointArray) {

        int index = 0;

        for (int point : pointArray[0]) {

            if ((point >= 0 && point < 10)
                && (pointArray[1][index] >= 0 && pointArray[1][index] < 10)) {

                String mapPoint = String.valueOf(point) + String.valueOf(pointArray[1][index]);
                mapPointList.add(mapPoint);
            }

            index++;
        }

        return mapPointList;
    }

    private static int[] writeDeepPoint(List<String> mapPointList) {

        Map<String, Integer> pointMap = new HashMap<String, Integer>();
        int deepPoint = 1;

        // 最も濃い座標を計算
        for (String point : mapPointList) {

            if (pointMap.containsKey(point)) {

                Integer number = pointMap.get(point);

                pointMap.remove(point);
                pointMap.put(point, number + 1);

                if (deepPoint < pointMap.get(point)) {

                    deepPoint = pointMap.get(point);
                }

            } else {

                pointMap.put(point, 1);
            }
        }

        // 滲んでいない座標数を計算
        int whitePoint = 100 - pointMap.size();

        int[] result = new int[] { whitePoint, deepPoint };

        return result;
    }

    private static int[][] largeDrop(int x, int y) {

        int[][] largeDropPointArray;
        largeDropPointArray = new int[2][];

        largeDropPointArray[0] =
            new int[] { x, x - 1, x, x + 1, x - 2, x - 1, x, x + 1, x + 2, x - 1, x, x + 1, x };
        largeDropPointArray[1] =
            new int[] { y - 2, y - 1, y - 1, y - 1, y, y, y, y, y, y + 1, y + 1, y + 1, y + 2 };

        return largeDropPointArray;
    }

    private static int[][] middleDrop(int x, int y) {

        int[][] middleDropPointArray;
        middleDropPointArray = new int[2][];

        middleDropPointArray[0] = new int[] { x - 1, x, x + 1, x - 1, x, x + 1, x - 1, x, x + 1 };
        middleDropPointArray[1] = new int[] { y - 1, y - 1, y - 1, y, y, y, y + 1, y + 1, y + 1 };

        return middleDropPointArray;
    }

    private static int[][] smallDrop(int x, int y) {

        int[][] smallDropPointArray;
        smallDropPointArray = new int[2][];

        smallDropPointArray[0] = new int[] { x, x - 1, x, x + 1, x };
        smallDropPointArray[1] = new int[] { y - 1, y, y, y, y + 1 };

        return smallDropPointArray;
    }
}

