					map[s[0]][s[1]+2]++; map[s[0]+4][s[1]+2]++; map[s[0]+2][s[1]]++;
					map[s[0]+2][s[1]+4]++;
				}
			}

			for(int i = 2; i < 12; i++){
				for(int j = 2; j < 12; j++){
					if( map[i][j] != 0 ){
