import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {
    static int[][] math;
    static final int MATH_LENGTH = 10;

    public static void main(String[] args) throws IOException {
        //紙
	math = new int[10][10];
        int scale;

        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String inputStr = null;
        while (true) {
            inputStr = br.readLine();
            if (inputStr == null || inputStr.isEmpty()) {
                break;
            }

            String[] inputSplitStr = inputStr.split(",");
            scale = Integer.valueOf(inputSplitStr[2]);

            switch (scale) {
            case 1: //インキ滴「小」
                setSmallInk(Integer.valueOf(inputSplitStr[0]), Integer.valueOf(inputSplitStr[1]);
                break;
            case 2: //インキ滴「中」
                setMediumInk(Integer.valueOf(inputSplitStr[0]), Integer.valueOf(inputSplitStr[1]);
                break;
            case 3: //インキ滴「大」
                setLargeInk(Integer.valueOf(inputSplitStr[0]), Integer.valueOf(inputSplitStr[1]);
                break;
            }
        }
        //集計
        int whiteNumber = 0;
        int mostNumber = 0;
        for (int i = 0; i < MATH_LENGTH; i++) {
            for (int j = 0; j < MATH_LENGTH; j++) {
                if (math[i][j] == 0) {
                    whiteNumber++;
                }
                if (mostNumber < math[i][j]) {
                    mostNumber = math[i][j];
                }
            }
        }
        System.out.println(whiteNumber);
        System.out.println(mostNumber);
    }

    private static void setSmallInk(int x, int y) {
        math[x][y]++;
        if ((x - 1) >= 0) {
            math[x - 1][y]++;
        }
        if ((y - 1) >= 0) {
            math[x][y - 1]++;
        }
        if (MATH_LENGTH > (x + 1)) {
            math[x + 1][y]++;
        }
        if (MATH_LENGTH > (y + 1)) {
            math[x][y + 1]++;
        }
    }

    private static void setMediumInk(int x, int y) {
        math[x][y]++;
        if ((x - 1) >= 0) {
            math[x - 1][y]++;
            if ((y - 1) >= 0) {
                math[x - 1][y - 1]++;
            }
            if (MATH_LENGTH > (y + 1)) {
                math[x - 1][y + 1]++;
            }
        }
        if ((y - 1) >= 0) {
            math[x][y - 1]++;
        }
        if (MATH_LENGTH > (y + 1)) {
            math[x][y + 1]++;
        }
        if (MATH_LENGTH > (x + 1)) {
            math[x + 1][y]++;
            if ((y - 1) >= 0) {
                math[x + 1][y - 1]++;
            }
            if (MATH_LENGTH > (y + 1)) {
                math[x + 1][y + 1]++;
            }
        }
    }

    private static void setLargeInk(int x, int y) {
        math[x][y]++;
        if ((x - 2) >= 0) {
            math[x - 2][y]++;
        }
        if (MATH_LENGTH > (x + 2)) {
            math[x + 2][y]++;
        }
        if ((y - 2) >= 0) {
            math[x][y - 2]++;
        }
        if (MATH_LENGTH > (y + 2)) {
            math[x][y + 2]++;
        }
        if ((x - 1) >= 0) {
            math[x - 1][y]++;
            if ((y - 1) >= 0) {
                math[x - 1][y - 1]++;
            }
            if (MATH_LENGTH > (y + 1)) {
                math[x - 1][y + 1]++;
            }
        }
        if ((y - 1) >= 0) {
            math[x][y - 1]++;
        }
        if (MATH_LENGTH > (y + 1)) {
            math[x][y + 1]++;
        }
        if (MATH_LENGTH > (x + 1)) {
            math[x + 1][y]++;
            if ((y - 1) >= 0) {
                math[x + 1][y - 1]++;
            }
            if (MATH_LENGTH > (y + 1)) {
                math[x + 1][y + 1]++;
            }
        }
    }
}

import java.util.Scanner;
public class Main {
   public static final int MAPSIZE = 10;
   public static void main(String[] args) {
      Scanner sc = new Scanner(System.in);
      /*we prepare bigger field*/
      int[][] field = new int[MAPSIZE + 4][MAPSIZE + 4];

      while(true) {
         /*get coordinates and magnitude of the ink*/
         String[] input = sc.nextLine().split(",");      

         /*if null input, break the loop*/
         if(input.length == 1) {
            break;
         }         

         /*parse the input data to integer*/
         int x = Integer.parseInt(input[0]);
         int y = Integer.parseInt(input[1]);
         int size = Integer.parseInt(input[2]);

         /*drop the ink*/
         drop(field, x, y, size);         
      }// end of while loop

      /*only go through valid part of the field and count value with 0*/
      int count = 0;
      int max = 0;
      for(int i = 2; i <= MAPSIZE + 1; i ++) {
         for(int j = 2; j <= MAPSIZE + 1; j ++) {
            if(field[i][j] == 0) {
               count ++;
            } else if(field[i][j] > max) {
               max = field[i][j];
            }
         }
      }

      /*print out the reuslt*/
      System.out.println(count);
      System.out.println(max);
   }// end of main method


   public static void drop(int[][] field, int x, int y, int size) {
      switch(size) {
         case 1:
            dropMin(field, y, x); //because the array is configured differently, x and y are switched.
            break;
         case 2:
            dropMiddle(field, y, x);
            break;
         case 3:
            dropMax(field, y, x);
            break;
      }
   }


   public static void dropMin(int[][] field, int x, int y) {
         field[x + 2][y + 2] ++; //center
         field[x + 2][y + 1] ++; //up
         field[x + 2][y + 3] ++; //down
         field[x + 1][y + 2] ++; //left
         field[x + 3][y + 2] ++; //right
   }

   public static void dropMiddle(int[][] field, int x, int y) {
      dropMin(field, x, y);
      field[x + 3][y + 3] ++;
      field[x + 3][y + 1] ++;
      field[x + 1][y + 1] ++;
      field[x + 1][y + 3] ++;
   }

   public static void dropMax(int[][] field, int x, int y) {
      dropMiddle(field, x, y);
      field[x + 2][y] ++;
      field[x + 2][y + 4] ++;
      field[x][y + 2] ++;
      field[x + 4][y + 2] ++;
   }

}// end of class

import java.util.*;
public class Main {
	public static void main(String[] args) {
		Scanner stdIn = new Scanner(System.in);
		int[][] p = new int[10][10]; //x,y
		while(stdIn.hasNext()) {
			String tmp = stdIn.nextLine();
			String[] k = tmp.split(",");
			int x = Integer.parseInt(k[0]);
			int y = Integer.parseInt(k[1]);
			int size = Integer.parseInt(k[2]);

			if(size >= 1) {
				if(check(x,y)) {
					p[x][y]++;
				}
				if(check(x+1,y)) {
					p[x+1][y]++;
				}
				if(check(x-1,y)) {
					p[x-1][y]++;
				}
				if(check(x,y+1)) {
					p[x][y+1]++;
				}
				if(check(x,y-1)) {
					p[x][y-1]++;
				}
			}
			if(size >= 2) {
				if(check(x+1,y+1)) {
					p[x+1][y+1]++;
				}
				if(check(x-1,y-1)) {
					p[x-1][y-1]++;
				}
				if(check(x+1,y-1)) {
					p[x+1][y-1]++;
				}
				if(check(x-1,y+1)) {
					p[x-1][y+1]++;
				}
			}
			if(size >= 3) {
				if(check(x+2,y)) {
					p[x+2][y]++;
				}
				if(check(x-2,y)) {
					p[x-2][y]++;
				}
				if(check(x,y+2)) {
					p[x][y+2]++;
				}
				if(check(x,y-2)) {
					p[x][y-2]++;
				}
			}
		}
		int whiteCounter = 0;
		int max = 0;
		for(int i = 0; i < 10; i++) {
			for(int j = 0; j < 10; j++) {
				if(p[i][j] > max) {
					max = p[i][j];
				}
				if(p[i][j] == 0) {
					whiteCounter++;
				}
			}
		}
		System.out.println(whiteCounter+"\n"+max);
	}
	static boolean check(int x, int y) {
		if(x < 0 || x > 9 || y < 0 || y > 9) {
			return false;
		}
		return true;
	}
}

import java.util.*;
