public class check
{
	static int array[][]= new int[10][10];
	public static void main (String[] arg)throws IOException
	{
		BufferedReader input =new BufferedReader (new InputStreamReader (System.in));
		String line;
		StringTokenizer tokenizer1,tokenizer2;
		int a[] = new int[3];
		int max=0, sum=0;

		while ((line = input.readLine()) != null && !line.isEmpty()) {
			tokenizer1 = new StringTokenizer(line,",");
			for(int i=0;i<3;i++){
				a[i]=Integer.parseInt(tokenizer1.nextToken());
			}
			if(a[2]==1){
				small(a[0],a[1]);
			}
			if(a[2]==2){
				mid(a[0],a[1]);
			}
			if(a[2]==3){
				big(a[0],a[1]);
			}
		}
		for(int i=0;i<10;i++){
			for(int j=0;j<10;j++){			
				max=Math.max(max, array[i][j]);
				if(array[i][j]==0){
					sum++;	
				}
			}
		}
		System.out.println(sum);
		System.out.println(max);
	}
	public static void small (int a, int b){
		int a2,b2;
		for(int i=0;i<3;i++){
			a2=a+i-1;			
			if (a2 > -1 && a2<10 ){
				array[a2][b]++;							
			}
		}
		for(int i=0;i<3;i++){
			b2=b+i-1;			
			if (b2 > -1 && b2<10 ){
				array[a][b2]++;							
			}
		}
		array[a][b]--;
	}
	public static void mid (int a, int b){
		int a2,b2;
		for(int i=0;i<3;i++){
			a2=a+i-1;			
			for(int j=0;j<3;j++){
				b2=b+j-1;
				if (a2 > -1 && a2< 10 && b2 > -1 && b2 < 10){
					array[a2][b2]++;	
				}
			}
		}
	}
	public static void big (int a, int b){
		mid(a,b);
		int i=2;
		int a2,b2;
		a2=i+a;
		if (a2 > -1 && a2<10){
			array[a2][b]++;	
		}
		a2=-i+a;
		if (a2 > -1 && a2<10){
			array[a2][b]++;	
		}
		b2=i+b;
		if (b2 > -1 && b2<10){
			array[a][b2]++;	
		}
		b2=-i+b;
		if (b2 > -1 && b2<10){
			array[a][b2]++;	
		}
	}
}

import java.util.Scanner;
public class Main {
   public static final int MAPSIZE = 10;
   public static void main(String[] args) {
      Scanner sc = new Scanner(System.in);
      /*we prepare bigger field*/
      int[][] field = new int[MAPSIZE + 4][MAPSIZE + 4];

      while(true) {
         /*get coordinates and magnitude of the ink*/
         String[] input = sc.nextLine().split(",");      

         /*if null input, break the loop*/
         if(input.length == 1) {
            break;
         }         

         /*parse the input data to integer*/
         int x = Integer.parseInt(input[0]);
         int y = Integer.parseInt(input[1]);
         int size = Integer.parseInt(input[2]);

         /*drop the ink*/
         drop(field, x, y, size);         
      }// end of while loop

      /*only go through valid part of the field and count value with 0*/
      int count = 0;
      int max = 0;
      for(int i = 2; i <= MAPSIZE + 1; i ++) {
         for(int j = 2; j <= MAPSIZE + 1; j ++) {
            if(field[i][j] == 0) {
               count ++;
            } else if(field[i][j] > max) {
               max = field[i][j];
            }
         }
      }

      /*print out the reuslt*/
      System.out.println(count);
      System.out.println(max);
   }// end of main method


   public static void drop(int[][] field, int x, int y, int size) {
      switch(size) {
         case 1:
            dropMin(field, y, x); //because the array is configured differently, x and y are switched.
            break;
         case 2:
            dropMiddle(field, y, x);
            break;
         case 3:
            dropMax(field, y, x);
            break;
      }
   }


   public static void dropMin(int[][] field, int x, int y) {
         field[x + 2][y + 2] ++; //center
         field[x + 2][y + 1] ++; //up
         field[x + 2][y + 3] ++; //down
         field[x + 1][y + 2] ++; //left
         field[x + 3][y + 2] ++; //right
   }

   public static void dropMiddle(int[][] field, int x, int y) {
      dropMin(field, x, y);
      field[x + 3][y + 3] ++;
      field[x + 3][y + 1] ++;
      field[x + 1][y + 1] ++;
      field[x + 1][y + 3] ++;
   }

   public static void dropMax(int[][] field, int x, int y) {
      dropMiddle(field, x, y);
      field[x + 2][y] ++;
      field[x + 2][y + 4] ++;
      field[x][y + 2] ++;
      field[x + 4][y + 2] ++;
   }

}// end of class

import java.util.*;

public class Main {

    public static void main (String[] args) {
        Scanner scanner = new Scanner(System.in);
        int[][] paper = new int[10][10];
        int max = 0;
        int counter = 0;

        for (int i = 0; i < paper.length; i++) {
            for (int j = 0; j < paper[i].length; j++) {
                paper[i][j] = 0;
            }
        }

        while (scanner.hasNext()) {
            String strLine = scanner.next();
            String[] str = strLine.split(",");

            int x = Integer.parseInt(str[0]);
            int y = Integer.parseInt(str[1]);
            int size = Integer.parseInt(str[2]);

            paper[x][y]++;
            if (isInsidePaper(x, y-1)) paper[x][y-1]++;
            if (isInsidePaper(x-1, y)) paper[x-1][y]++;
            if (isInsidePaper(x+1, y)) paper[x+1][y]++;
            if (isInsidePaper(x, y+1)) paper[x][y+1]++;

            if (size >= 2) {
                if (isInsidePaper(x-1, y-1))    paper[x-1][y-1]++;
                if (isInsidePaper(x+1, y-1))    paper[x+1][y-1]++;
                if (isInsidePaper(x-1, y+1))    paper[x-1][y+1]++;
                if (isInsidePaper(x+1, y+1))    paper[x+1][y+1]++;
            }
            if (size == 3) {
                if (isInsidePaper(x, y-2))    paper[x][y-2]++;
                if (isInsidePaper(x-2, y))    paper[x-2][y]++;
                if (isInsidePaper(x+2, y))    paper[x-2][y]++;
                if (isInsidePaper(x, y+2))    paper[x][y+2]++;
            }
        }
        for (int i = 0; i < paper.length; i++) {
            for (int j = 0; j < paper[i].length; j++) {
                if (paper[i][j] == 0) {
                    counter++;
                } else {
                    if (paper[i][j] > max) max = paper[i][j];
                }
            }
        }
        System.out.println(counter);
        System.out.println(max);
    }

    public static boolean isInsidePaper(int x, int y) {
        if (x >= 0 && x <= 9 && y >= 0 && y <= 9) {
            return true;
        } else {
            return false;
        }
    }
}

import java.io.IOException;
import java.util.Scanner;
import java.util.StringTokenizer;

public class Main {

	private static Scanner sc;
	private static int[][] squares;

	static {
		sc = new Scanner(System.in);
		squares = new int[10][10];
	}

	public static void main(String[] args) throws IOException {

		String line;
		int x, y, size, blind = 100, max = 0;

		while ((line = sc.nextLine()) != null && !line.isEmpty()) {
			StringTokenizer st = new StringTokenizer(line, ",");

			while (st.hasMoreTokens()) {
				x = Integer.parseInt(st.nextToken());
				y = Integer.parseInt(st.nextToken());
				size = Integer.parseInt(st.nextToken());

				squares[x][y]++;
				switch (size) {
				case 1:
					set1(x, y);
					break;
				case 2:
					set1(x, y);
					set2(x, y);
					break;
				case 3:
					set1(x, y);
					set2(x, y);
					set3(x, y);
				default:
					break;
				}
			}
		}
		for (int[] square : squares) {
			for (int value : square) {
				if (value != 0) {
					blind--;
				}
				if (max < value) {
					max = value;
				}
			}
		}
		System.out.println(blind + "\n" + max);
	}

	private static void set1(int x, int y) {
		if (x > 0) {
			squares[x - 1][y]++;
		}
		if (x < 9) {
			squares[x + 1][y]++;
		}
		if (y > 0) {
			squares[x][y - 1]++;
		}
		if (y < 9) {
			squares[x][y + 1]++;
		}
	}

	private static void set2(int x, int y) {
		if (x > 0 && y > 0) {
			squares[x - 1][y - 1]++;
		}
		if (x > 0 && y < 9) {
			squares[x - 1][y + 1]++;
		}
		if (x < 9 && y > 0) {
			squares[x + 1][y - 1]++;
		}
		if (x < 9 && y < 10) {
			squares[x + 1][y + 1]++;
		}
	}

	private static void set3(int x, int y) {
		if (x - 1 > 0) {
			squares[x - 2][y]++;
		}
		if (x + 1 < 9) {
			squares[x + 2][y]++;
		}
		if (y - 1 > 0) {
			squares[x][y - 2]++;
		}
		if (y + 1 < 9) {
			squares[x][y + 2]++;
		}
	}
}

