    int h = 10;
    int w = 10;
    int[] a = {1, 0, -1, 0, 1, -1, 1, -1, 2, 0, -2, 0};
    int[] b = {0, 1, 0, -1, 1, 1, -1, -1, 0, 2, 0, -2};
    // 3まで小、7まで中、11まで大
    int count;
    int max;

    void run(){
	map = new int[h][w];
