import java.awt.Point;
import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.HashSet;

public class Main {
    public static void main(String[] args) {
        int x = 0;
        int y = 0;
        int scale = 0;
        // 紙[10×10]。
        int[][] math = new int[10][10];
        // インクが落ちた座標。
        HashSet<Point> scopes = new HashSet<Point>();

        // 入力値を取得
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        while (true) {
            String inputStr = null;
            try {
                inputStr = br.readLine();
            } catch (IOException e) {
                e.printStackTrace();
            }
            if (inputStr == null) {
                break;
            }
            // 入力文字列を","で分割
            String[] inputSplitStr = inputStr.split(",");
            x = Integer.valueOf(inputSplitStr[0]);
            y = Integer.valueOf(inputSplitStr[1]);
            scale = Integer.valueOf(inputSplitStr[2]);

            // インク滴が滲む範囲の計算
            scopes.clear();
            switch (scale) {
            case 1: // インキ滴「小」
                scopes = calSmallScopes(x, y, scopes);
                break;
            case 2: // インキ滴「中」
                scopes = calMediumScopes(x, y, scopes);
                break;
            case 3: // インキ滴「大」
                scopes = calLargeScopes(x, y, scopes);
                break;
            }

            // インク滴がマスの範囲内(10×10)であれば配列の値に1を加算する。
            for (Point scope : scopes) {
                if (0 <= scope.x && scope.x < 10 && 0 <= scope.y && scope.y < 10) {
                    math[scope.x][scope.y]++;
                }
            }
        }

        // 色のついていない部分のマス目の個数と１番濃いマス目の濃さを計算
        int whiteNumber = 0;
        int mostNumber = 0;
        for (int i = 0; i < 10; i++) {
            for (int j = 0; j < 10; j++) {
                if (math[i][j] == 0) {
                    whiteNumber++;
                }
                if (mostNumber < math[i][j]) {
                    mostNumber = math[i][j];
                }
            }
        }
        System.out.println(whiteNumber);
        System.out.println(mostNumber);
    }

    private static HashSet<Point> calSmallScopes(int x, int y, HashSet<Point> scopes) {
        scopes.add(new Point(x - 1, y));
        scopes.add(new Point(x, y - 1));
        scopes.add(new Point(x, y));
        scopes.add(new Point(x, y + 1));
        scopes.add(new Point(x + 1, y));
        return scopes;
    }

    private static HashSet<Point> calMediumScopes(int x, int y, HashSet<Point> scopes) {
        scopes.add(new Point(x - 1, y - 1));
        scopes.add(new Point(x - 1, y));
        scopes.add(new Point(x - 1, y + 1));

        scopes.add(new Point(x, y - 1));
        scopes.add(new Point(x, y));
        scopes.add(new Point(x, y + 1));

        scopes.add(new Point(x + 1, y - 1));
        scopes.add(new Point(x + 1, y));
        scopes.add(new Point(x + 1, y + 1));
        return scopes;
    }

    private static HashSet<Point> calLargeScopes(int x, int y, HashSet<Point> scopes) {
        scopes.add(new Point(x - 2, y));

        scopes.add(new Point(x - 1, y - 1));
        scopes.add(new Point(x - 1, y));
        scopes.add(new Point(x - 1, y + 1));

        scopes.add(new Point(x, y - 2));
        scopes.add(new Point(x, y - 1));
        scopes.add(new Point(x, y));
        scopes.add(new Point(x, y + 1));
        scopes.add(new Point(x, y + 2));

        scopes.add(new Point(x + 1, y - 1));
        scopes.add(new Point(x + 1, y));
        scopes.add(new Point(x + 1, y + 1));

        scopes.add(new Point(x + 2, y));
        return scopes;
    }
}

import java.io.IOException;
import java.io.BufferedReader;
import java.io.InputStreamReader;

public class Main
{
    public static void main(String[] args) throws IOException
    {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String s;
        int[][] paper = new int[14][14];
        // assume we use 14*14 cells, then finally ignore the 0 to 1 and 12 to 13
        int count=0, max=0;

        while((s = br.readLine())!=null && !s.isEmpty())
        {
            int x = Integer.parseInt(s.split(",")[0]);
            int y = Integer.parseInt(s.split(",")[1]);
            int size = Integer.parseInt(s.split(",")[2]);

            drop(x+2,y+2,size,paper); // as we use 14*14 paper(but just assumes it as 10*10)
        }
        for(int i=2; i<12; i++)
            for(int j=2; j<12; j++){
                if(paper[i][j]==0) count++;
                max = Math.max(paper[i][j],max);
            }
        System.out.println(count);
        System.out.println(max);
    }

    private static void drop(int x, int y, int size, int[][] paper)
    {
        if(size==1){
            for(int i=x-1; i<x+2; i++) paper[y][i]++;
            for(int i=y-1; i<y+2; i++) paper[i][x]++;
            paper[y][x]--;
        } else if(size==2) {
            for(int i=x-1; i<x+2; i++)
                for(int j=y-1; j<y+2; j++)
                    paper[j][i]++;
        } else {
            for(int i=x-2; i<x+3; i++) paper[y][i]++;
            for(int i=x-1; i<x+2; i++) {
                paper[y+1][i]++;
                paper[y-1][i]++;
            }
            for(int i=y-2; i<y+3; i++) paper[i][x]++;
                paper[y+1][x]--;
                paper[y-1][x]--;
                paper[y][x]--;
        }
    }
}

import java.io.*;
import java.util.StringTokenizer;

class DroppingMat {
    int mat[][] = new int[10][10];

    DroppingMat() {
	for (int i=0;i<10;i++) {
	    for (int j=0;j<10;j++) this.mat[i][j] = 0;
	}
    }

    void Drop(int x,int y) {
	if (0<=x && x<=9 && 0<=y && y<=9) this.mat[x][y]++;
    }

    void DropSmallInk(int x,int y) {
	Drop(x,y);
	Drop(x-1,y);
	Drop(x+1,y);
	Drop(x,y-1);
	Drop(x,y+1);
    }

    void DropMidiumInk(int x,int y) {
	Drop(x-1,y-1);
	Drop(x+1,y-1);
	Drop(x-1,y+1);
	Drop(x+1,y+1);
	DropSmallInk(x,y);
    }

    void DropLargeInk(int x,int y) {
	Drop(x-2,y);
	Drop(x+2,y);
	Drop(x,y-2);
	Drop(x,y+2);
	DropMidiumInk(x,y);
    }

    int NumberofZero() {
	int count = 0;
	for (int i=0;i<10;i++)
	    for (int j=0;j<10;j++)
		if (this.mat[i][j]==0) count++;
	return count;
    }

    int MaxValue() {
	int max = 0;
	for (int i=0;i<10;i++)
	    for (int j=0;j<10;j++)
		if (this.mat[i][j]>max) max = this.mat[i][j];
	return max;
    }

    //テスト用
    void PrintMat() {
	for (int i=0;i<10;i++) {
	    for (int j=0;j<10;j++) System.out.print(this.mat[i][j]+" ");
	    System.out.println(" ");
	}
    }
}

class Main {
    public static void main(String args[]) {
	BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
	String buf;
	DroppingMat dm = new DroppingMat();

	try {
	    while ((buf = br.readLine())!=null) {
		StringTokenizer st = new StringTokenizer(buf,",");
		int x = Integer.parseInt(st.nextToken());
		int y = Integer.parseInt(st.nextToken());
		int size = Integer.parseInt(st.nextToken());

	        switch (size) {
		case 1: dm.DropSmallInk(x,y); break;
		case 2: dm.DropMidiumInk(x,y); break;
		case 3: dm.DropLargeInk(x,y); break;
		}
		//dm.PrintMat();
	    }
	    System.out.println(dm.NumberofZero());
	    System.out.println(dm.MaxValue());
	} catch (Exception e) {
	    e.printStackTrace();
	}
    } 
}

import java.util.HashMap;
import java.util.Iterator;
import java.util.Map;
import java.util.Map.Entry;
import java.util.Scanner;

public class Main {

	/**
	 * @param args
	 */
	public static void main(String[] args) {

		Scanner sc = new Scanner(System.in);

		Map<String, Integer> dropingMap = new HashMap<String, Integer>();
		for (int x = 0; x < 10; x++) {
			for (int y = 0; y < 10; y++) {
				dropingMap.put(String.valueOf(x).concat(String.valueOf(y)), 0);
			}
		}
		while (sc.hasNext()) {
			String[] s = sc.next().split(",");
			int dx = Integer.parseInt(s[0]);
			int dy = Integer.parseInt(s[1]);
			int d = Integer.parseInt(s[2]);

			if (d == 1) {
				dropMin(dx, dy, dropingMap);
			} else if (d == 2) {
				dropMid(dx, dy, dropingMap);
			} else {
				dropMax(dx, dy, dropingMap);
			}
		}
		checkMax(dropingMap);
	}

	static void checkMax(Map<String, Integer> dropingMap) {

		int maxVal = 0;
		int coutZero = 0;
		for (Iterator<Entry<String, Integer>> dropMap = dropingMap.entrySet().iterator(); dropMap.hasNext();) {
			Entry<String, Integer> entry = dropMap.next();
			int value = (Integer) entry.getValue();

			if (maxVal < value) {
				maxVal = value;
			}
			if (value == 0) {
				coutZero = coutZero + 1;
			}
		}
		System.out.println(coutZero);
		System.out.println(maxVal);

	}

	//String　の1桁をx 2桁をy
	static void dropMin(int x, int y, Map<String, Integer> dropMapData) {

		for (Iterator<Entry<String, Integer>> dropMap = dropMapData.entrySet().iterator(); dropMap.hasNext();) {
			Entry<String, Integer> entry = dropMap.next();
			String key = (String) entry.getKey();
			refreshMap(x, y, entry, key);

			//十字
			refreshMap(x, y + 1, entry, key);
			refreshMap(x, y - 1, entry, key);
			refreshMap(x + 1, y, entry, key);
			refreshMap(x - 1, y, entry, key);
		}
	}

	//String　の1桁をx 2桁をy
	static void dropMid(int x, int y, Map<String, Integer> dropMapData) {

		for (Iterator<Entry<String, Integer>> dropMap = dropMapData.entrySet().iterator(); dropMap.hasNext();) {
			Entry<String, Integer> entry = dropMap.next();
			String key = (String) entry.getKey();
			refreshMap(x, y, entry, key);

			//十字+ななめ
			refreshMap(x, y + 1, entry, key);
			refreshMap(x, y - 1, entry, key);
			refreshMap(x + 1, y, entry, key);
			refreshMap(x - 1, y, entry, key);
			refreshMap(x + 1, y + 1, entry, key);
			refreshMap(x - 1, y - 1, entry, key);
			refreshMap(x + 1, y - 1, entry, key);
			refreshMap(x - 1, y + 1, entry, key);
		}

	}

	//String　の1桁をx 2桁をy
	static void dropMax(int x, int y, Map<String, Integer> dropMapData) {

		for (Iterator<Entry<String, Integer>> dropMap = dropMapData.entrySet().iterator(); dropMap.hasNext();) {
			Entry<String, Integer> entry = dropMap.next();
			String key = (String) entry.getKey();
			refreshMap(x, y, entry, key);

			//十字+ななめ
			refreshMap(x, y + 1, entry, key);
			refreshMap(x, y - 1, entry, key);
			refreshMap(x + 1, y, entry, key);
			refreshMap(x - 1, y, entry, key);

			refreshMap(x, y + 2, entry, key);
			refreshMap(x, y - 2, entry, key);
			refreshMap(x + 2, y, entry, key);
			refreshMap(x - 2, y, entry, key);

			refreshMap(x + 1, y + 1, entry, key);
			refreshMap(x - 1, y - 1, entry, key);
			refreshMap(x + 1, y - 1, entry, key);
			refreshMap(x - 1, y + 1, entry, key);
		}

	}

	static void refreshMap(int x, int y, Entry<String, Integer> entry, String key) {
		if (-1 < x && x < 10 && -1 < y && y < 10 && key.equals((String.valueOf(x)).concat(String.valueOf(y)))) {
			entry.setValue((Integer) entry.getValue() + 1);
		}
	}
}

