            if ((y - 1) >= 0) {
                math[x + 1][y - 1]++;
            }
            if (MATH_LENGTH > (y + 1)) {
                math[x + 1][y + 1]++;
            }
        }
    }
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {
	private final byte MASK_RIGHT = 0x0F;//右4bit数値化用マスク
	private int[][] paper = new int[10][10];//都合の良い方眼紙

	public static void main(String[] args) throws NumberFormatException, IOException {
		new Main().execute();
	}

	private void execute() throws NumberFormatException, IOException {
		BufferedReader reader = new BufferedReader(new InputStreamReader(System.in), 128);
		String sLine;
		while ((sLine = reader.readLine()) != null) {
			droppingInk(sLine.charAt(0) & MASK_RIGHT, sLine.charAt(2) & MASK_RIGHT, sLine.charAt(4) & MASK_RIGHT);
		}
		calcResult();
	}

	/**
	 *都合の良い滴下
	 */
	private void droppingInk(int x, int y, int size) {
		sinking(x, y - 1);
		sinking(x - 1, y);
		sinking(x, y);
		sinking(x + 1, y);
		sinking(x, y + 1);
		if (size == 1) {
			return;
		}

		sinking(x - 1, y - 1);
		sinking(x + 1, y - 1);
		sinking(x - 1, y + 1);
		sinking(x + 1, y + 1);
		if (size == 2) {
			return;
		}

		sinking(x, y - 2);
		sinking(x - 2, y);
		sinking(x + 2, y);
		sinking(x, y + 2);
	}

	/**
	 * 床は汚さないようにする
	 */
	private void sinking(int x, int y) {
		if (x >= 0 && x < 10 && y >= 0 && y < 10) {
			paper[x][y]++;
		}
	}

	/**
	 *紙の染みを数え、損害を報告する
	 */
	private void calcResult() {
		int white = 0;//綺麗な部分
		int max = 0;//一番ひどい汚れ
		for (int[] row : paper) {
			for (int colum : row) {
				if (colum == 0) {
					white++;
				} else if (colum > max) {
					max = colum;
				}
			}
		}
		System.out.println(white);
		System.out.println(max);
	}
}

import java.util.*;
public class Main {
	int[][] dp = new int[10][10];

	void small(int x, int y) {
		dp[x][y]++;
		if(x + 1 < 10) {
			dp[x + 1][y]++;

		}
		if(y + 1 < 10) {
			dp[x][y + 1]++;

		}
		if(x - 1 >= 0) {
			dp[x - 1][y]++;

		}
		if(y - 1 >= 0) {
			dp[x][y - 1]++;

		}
	}

	void medium(int x, int y) {
		small(x, y);
		if(x + 1 < 10 && y + 1 < 10) {
			dp[x + 1][y + 1]++;

		}
		if(x - 1 >= 0 && y + 1 < 10) {
			dp[x - 1][y + 1]++;

		}
		if(x + 1 < 10 && y - 1 >= 0) {
			dp[x + 1][y - 1]++;

		}
		if(x - 1 >= 0 && y - 1 >= 0) {
			dp[x - 1][y - 1]++;

		}
	}

	void large(int x, int y) {
		medium(x, y);
		if(x + 2 < 10) {
			dp[x + 2][y]++;

		}
		if(y + 2 < 10) {
			dp[x][y + 2]++;

		}
		if(x - 2 >= 0) {
			dp[x - 2][y]++;

		}
		if(y - 2 >= 0) {
			dp[x][y - 2]++;

		}
	}

	void doIt() {
		Scanner stdIn = new Scanner(System.in);
		int max = Integer.MIN_VALUE;
		int count = 0;
		while(stdIn.hasNext()) {
			String input = stdIn.next();
			int x = input.charAt(0) - '0';
			int y = input.charAt(2) - '0';
			int mode = input.charAt(4) - '0';
			switch(mode) {
			case 1: small(x, y); break;
			case 2: medium(x, y); break;
			case 3: large(x, y); break;
			}
		}
		for(int r = 0; r < 10; r++) {
			for(int c = 0; c < 10; c++) {
				if(dp[r][c] != 0) {
					max = Math.max(max, dp[r][c]);
					continue;
				}
				count++;
			}
		}
		System.out.println(count + "\n" + max);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		new Main().doIt();
	}

}

import java.io.IOException;
import java.util.Scanner;

public class Main {
	static int[][] board = new int[10][10];

	public static void main(String[] args) throws IOException {
		// 宣言
		Scanner sc = new Scanner(System.in);
		String[] str;
		int count = 0;
		int max = 0;

		while (sc.hasNext()) {
			str = sc.nextLine().split(",");
			int x = Integer.parseInt(str[0]);
			int y = Integer.parseInt(str[1]);
			int size = Integer.parseInt(str[2]);
			if (size == 1) {
				small(x, y);
			} else if (size == 2) {
				middle(x, y);
			} else if (size == 3) {
				big(x, y);
			}
		}
		for (int xx = 0; xx < 10; xx++) {
			for (int yy = 0; yy < 10; yy++) {
				// 1個目の答え
				if (board[xx][yy] == 0) {
					count++;
				}
				// 2個目の答え
				if (max < board[xx][yy]) {
					max = board[xx][yy];
				}
			}
		}
		System.out.println(count);
		System.out.println(max);
	}

	// インク小　中心と上下左右1マス
	static void small(int x, int y) {
		paint(x, y);
		// 左右、上下のマスを塗る
		paint(x + 1, y);
		paint(x - 1, y);
		paint(x, y - 1);
		paint(x, y + 1);
	}
	// インク中　上下左右斜め9マス
	static void middle(int x, int y) {
		for (int yr = -1; yr <= 1; yr++) {
			for (int xr = -1; xr <= 1; xr++) {
				paint(x + xr, y + yr);
			}
		}
	}
	//　インク大　インク中＋上下左右2マス
	static void big(int x, int y) {
		middle(x, y);
		//左右、上下の2マス目を塗る
		paint(x, y - 2);
		paint(x, y + 2);
		paint(x + 2, y);
		paint(x - 2, y);
	}

	static void paint(int x, int y) {
		//範囲外にあたる場合は何もしない
		if (x < 0 || y < 0 || x >= 10 || y >= 10) {
			return;
		} else {
			board[x][y] += 1;
		}

	}

}

