		}
		if (y != 9) {
			droppingInkList.add(joinString(x, y + 1));
		}
		if (x != 0) {
			droppingInkList.add(joinString(x - 1, y));
		}
		if (x != 9) {
			droppingInkList.add(joinString(x + 1, y));
