import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {

	public static void main(String[] args) throws IOException {
		// 10*10マス目の紙
		int paper[][] = new int[10][10];
		// 色がついてないマス目の個数
		int emptySpace = 0;
		// 1番濃いマス目の濃さ
		int max = 0;

		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		String str = br.readLine();

		while (str != null) {
			// 文字列を指定した区切り文字(",")によって分割し,それぞれの配列の要素として格納する
			String[] strList = str.split(",");
			int x = Integer.parseInt(strList[0]);
			int y = Integer.parseInt(strList[1]);
			int size = Integer.parseInt(strList[2]);

			// 滴下メソッド
			Main main = new Main();
			main.drop(paper, x, y, size);
		}

		// 色のついてない部分のマス目の個数と、1番濃いマス目の濃さを格納
		for (int i = 0; i < 10; i++) {
			for (int j = 0; j < 10; j++) {
				if (paper[i][j] == 0) {
					emptySpace += 1;
				}
				if (paper[i][j] > max) {
					max = paper[i][j];
				}
			}
		}

		System.out.println(emptySpace);
		System.out.println(max);
	}

	// ぞれぞれのサイズに合わせた滴下メソッド呼び出す
	public void drop(int paper[][], int x, int y, int size) {
		if (size == 1) {
			drop1(paper, x, y);
		}

		if (size == 2) {
			drop2(paper, x, y);
		}

		if (size == 3) {
			drop3(paper, x, y);
		}
	}

	// サイズ小の滴下メソッド
	public void drop1(int paper[][], int x, int y) {
		paper[x][y] += 1;
		if (x + 1 < 10) {
			paper[x + 1][y] += 1;
		}
		if (x - 1 > -1) {
			paper[x - 1][y] += 1;
		}
		if (y + 1 < 10) {
			paper[x][y + 1] += 1;
		}
		if (y - 1 > -1) {
			paper[x][y - 1] += 1;
		}
	}

	// サイズ中の滴下メソッド
	public void drop2(int paper[][], int x, int y) {
		drop1(paper, x, y);
		if (x + 1 < 10 && y + 1 < 10) {
			paper[x + 1][y + 1] += 1;
		}
		if (x + 1 < 10 && y - 1 > -1) {
			paper[x + 1][y - 1] += 1;
		}
		if (x - 1 > -1 && y + 1 < 10) {
			paper[x - 1][y + 1] += 1;
		}
		if (x - 1 > -1 && y - 1 > -1) {
			paper[x - 1][y - 1] += 1;
		}
	}

	// サイズ大の滴下メソッド
	public void drop3(int paper[][], int x, int y) {
		drop1(paper, x, y);
		drop2(paper, x, y);

		if (x + 2 < 10) {
			paper[x + 2][y] += 1;
		}
		if (x - 2 > -1) {
			paper[x - 2][y] += 1;
		}
		if (y + 2 < 10) {
			paper[x][y + 2] += 1;
		}
		if (y - 2 > -1) {
			paper[x][y - 2] += 1;
		}

	}

}

import java.util.*;
import static java.lang.Math.*;
import static java.util.Arrays.*;

public class Main {

	int INF = 1 << 28;
	int OFS = 2;
	int MAX = 10 + OFS * 2;
	int map[][];

	void run() {
		Scanner sc = new Scanner(System.in);
		map = new int[MAX][MAX];

		while(sc.hasNext()) {
			String[] str = sc.next().split(",");
			int[] v = new int[3];
			for(int i=0;i<3;i++) v[i] = Integer.parseInt(str[i]);
			if(v[2] == 1) s(v[0]+OFS, v[1]+OFS);
			if(v[2] == 2) m(v[0]+OFS, v[1]+OFS);
			if(v[2] == 3) l(v[0]+OFS, v[1]+OFS);
		}
		int max = 0, cnt = 0;
		for(int i=OFS;i<MAX-OFS;i++) for(int j=OFS;j<MAX-OFS;j++) {
			max = max(max, map[i][j]);
			if(map[i][j] == 0) cnt++;
		}
		System.out.println(cnt);
		System.out.println(max);
	}

	void s(int x, int y) {
		map[y][x]++; map[y-1][x]++; map[y][x-1]++; map[y+1][x]++; map[y][x+1]++; 
	}
	void m(int x, int y) {
		for(int i=x-1;i<=x+1;i++) for(int j=y-1;j<=y+1;j++)
			map[j][i]++;
	}
	void l(int x, int y) {
		m(x, y);
		map[y-2][x]++; map[y][x-2]++; map[y+2][x]++; map[y][x+2]++;
	}

	public static void main(String[] args) {
		new Main().run();
	}

	void debug(Object... os) {
		System.err.println(Arrays.deepToString(os));
	}
}

import java.util.Scanner;
public class Main {
   public static final int MAPSIZE = 10;
   public static void main(String[] args) {
      Scanner sc = new Scanner(System.in);
      /*we prepare bigger field*/
      int[][] field = new int[MAPSIZE + 4][MAPSIZE + 4];

      while(true) {
         /*get coordinates and magnitude of the ink*/
         String[] input = sc.nextLine().split(",");      

         /*if null input, break the loop*/
         if(input.length == 1) {
            break;
         }         

         /*parse the input data to integer*/
         int x = Integer.parseInt(input[0]);
         int y = Integer.parseInt(input[1]);
         int size = Integer.parseInt(input[2]);

         /*drop the ink*/
         drop(field, x, y, size);         
      }// end of while loop

      /*only go through valid part of the field and count value with 0*/
      int count = 0;
      int max = 0;
      for(int i = 2; i <= MAPSIZE + 1; i ++) {
         for(int j = 2; j <= MAPSIZE + 1; j ++) {
            if(field[i][j] == 0) {
               count ++;
            } else if(field[i][j] > max) {
               max = field[i][j];
            }
         }
      }

      /*print out the reuslt*/
      System.out.println(count);
      System.out.println(max);
   }// end of main method


   public static void drop(int[][] field, int x, int y, int size) {
      switch(size) {
         case 1:
            dropMin(field, y, x); //because the array is configured differently, x and y are switched.
            break;
         case 2:
            dropMiddle(field, y, x);
            break;
         case 3:
            dropMax(field, y, x);
            break;
      }
   }


   public static void dropMin(int[][] field, int x, int y) {
         field[x + 2][y + 2] ++; //center
         field[x + 2][y + 1] ++; //up
         field[x + 2][y + 3] ++; //down
         field[x + 1][y + 2] ++; //left
         field[x + 3][y + 2] ++; //right
   }

   public static void dropMiddle(int[][] field, int x, int y) {
      dropMin(field, x, y);
      field[x + 3][y + 3] ++;
      field[x + 3][y + 1] ++;
      field[x + 1][y + 1] ++;
      field[x + 1][y + 3] ++;
   }

   public static void dropMax(int[][] field, int x, int y) {
      dropMiddle(field, x, y);
      field[x + 2][y] ++;
      field[x + 2][y + 4] ++;
      field[x][y + 2] ++;
      field[x + 4][y + 2] ++;
   }

}// end of class

