							map[x-2+i][y-2+j] += drop3[i][j];
						}
						catch (ArrayIndexOutOfBoundsException e){continue;}
					}
				}
			}
		}
		int count = 0;
		int max = 0;
		for (int i = 0; i < 10; i++)
		{
			for (int j = 0; j < 10; j++)
			{
				if (map[i][j] == 0) count++;
				max = Math.max(max, map[i][j]);
			}
		}
		System.out.println(count);
		System.out.println(max);
	}
}

