import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {
    static int[][] math;
    static final int MATH_LENGTH = 10;

    public static void main(String[] args) throws IOException {
        //紙
	math = new int[10][10];
        int scale;

        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String inputStr = null;
        while (true) {
            inputStr = br.readLine();
            if (inputStr == null || inputStr.isEmpty()) {
                break;
            }

            String[] inputSplitStr = inputStr.split(",");
            scale = Integer.valueOf(inputSplitStr[2]);

            switch (scale) {
            case 1: //インキ滴「小」
                setSmallInk(Integer.valueOf(inputSplitStr[0]), Integer.valueOf(inputSplitStr[1]));
                break;
            case 2: //インキ滴「中」
                setMediumInk(Integer.valueOf(inputSplitStr[0]), Integer.valueOf(inputSplitStr[1]));
                break;
            case 3: //インキ滴「大」
                setLargeInk(Integer.valueOf(inputSplitStr[0]), Integer.valueOf(inputSplitStr[1]));
                break;
            }
        }
        //集計
        int whiteNumber = 0;
        int mostNumber = 0;
        for (int i = 0; i < MATH_LENGTH; i++) {
            for (int j = 0; j < MATH_LENGTH; j++) {
                if (math[i][j] == 0) {
                    whiteNumber++;
                }
                if (mostNumber < math[i][j]) {
                    mostNumber = math[i][j];
                }
            }
        }
        System.out.println(whiteNumber);
        System.out.println(mostNumber);
    }

    private static void setSmallInk(int x, int y) {
        math[x][y]++;
        if ((x - 1) >= 0) {
            math[x - 1][y]++;
        }
        if ((y - 1) >= 0) {
            math[x][y - 1]++;
        }
        if (MATH_LENGTH > (x + 1)) {
            math[x + 1][y]++;
        }
        if (MATH_LENGTH > (y + 1)) {
            math[x][y + 1]++;
        }
    }

    private static void setMediumInk(int x, int y) {
        math[x][y]++;
        if ((x - 1) >= 0) {
            math[x - 1][y]++;
            if ((y - 1) >= 0) {
                math[x - 1][y - 1]++;
            }
            if (MATH_LENGTH > (y + 1)) {
                math[x - 1][y + 1]++;
            }
        }
        if ((y - 1) >= 0) {
            math[x][y - 1]++;
        }
        if (MATH_LENGTH > (y + 1)) {
            math[x][y + 1]++;
        }
        if (MATH_LENGTH > (x + 1)) {
            math[x + 1][y]++;
            if ((y - 1) >= 0) {
                math[x + 1][y - 1]++;
            }
            if (MATH_LENGTH > (y + 1)) {
                math[x + 1][y + 1]++;
            }
        }
    }

    private static void setLargeInk(int x, int y) {
        math[x][y]++;
        if ((x - 2) >= 0) {
            math[x - 2][y]++;
        }
        if (MATH_LENGTH > (x + 2)) {
            math[x + 2][y]++;
        }
        if ((y - 2) >= 0) {
            math[x][y - 2]++;
        }
        if (MATH_LENGTH > (y + 2)) {
            math[x][y + 2]++;
        }
        if ((x - 1) >= 0) {
            math[x - 1][y]++;
            if ((y - 1) >= 0) {
                math[x - 1][y - 1]++;
            }
            if (MATH_LENGTH > (y + 1)) {
                math[x - 1][y + 1]++;
            }
        }
        if ((y - 1) >= 0) {
            math[x][y - 1]++;
        }
        if (MATH_LENGTH > (y + 1)) {
            math[x][y + 1]++;
        }
        if (MATH_LENGTH > (x + 1)) {
            math[x + 1][y]++;
            if ((y - 1) >= 0) {
                math[x + 1][y - 1]++;
            }
            if (MATH_LENGTH > (y + 1)) {
                math[x + 1][y + 1]++;
            }
        }
    }
}

import java.util.Scanner;
public class Main {
	static int bord[][]=new int[10][10];
	public static void main(String[] args) {
		Scanner s=new Scanner(System.in);
		int x,y,size,i,j,count=0,max=0;
		while(s.hasNext()){
			String[] str = s.next().split(",");
			x = Integer.parseInt(str[0]);
			y = Integer.parseInt(str[1]);
			size = Integer.parseInt(str[2]);
			if(size==1){
				small(x,y);
			}
			if(size==2){
				middle(x,y);
			}
			if(size==3){
				large(x,y);
			}
		}//while
		for(i=0;i<10;i++){
			for(j=0;j<10;j++){
				if(bord[i][j]==0)
					count++;
				if(bord[i][j]>max)
					max=bord[i][j];
			}
		}
		System.out.println(count);
		System.out.println(max);
	}//main
	public static void small(int x,int y){
		bord[x][y]++;
		if(x-1>-1)
			bord[x-1][y]++;
		if(x+1<10)
			bord[x+1][y]++;
		if(y-1>-1)
			bord[x][y-1]++;
		if(y+1<10)
			bord[x][y+1]++;
	}
	public static void middle(int x,int y){
		small(x,y);
		if(x-1>-1 && y-1>-1)
			bord[x-1][y-1]++;
		if(x+1<10 && y+1<10)
			bord[x+1][y+1]++;
		if(x+1<10 && y-1>-1)
			bord[x+1][y-1]++;
		if(x-1>-1 && y+1<10)
			bord[x-1][y+1]++;
	}
	public static void large(int x,int y){
		middle(x,y);
		if(x-2>-1)
			bord[x-2][y]++;
		if(x+2<10)
			bord[x+2][y]++;
		if(y-2>-1)
			bord[x][y-2]++;
		if(y+2<10)
			bord[x][y+2]++;
	}
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {

	private static int[][] square = new int[10][10];
	private static int mostDeep = 0;
	private static int whiteTiles = 0;

	/**
	 * 標準入力された値を受け取り、インクのサイズによって滲む範囲を切り分ける。
	 * すべての入力が終了した後にまだインクが滲んでいないマスの数と、最も濃いマスを判定し、出力する。
	 *
	 * @param args
	 * @throws IOException
	 */
	public static void main(String[] args) throws IOException {
		// TODO 自動生成されたメソッド・スタブ

		String[] inputString;
		String readLine = "";

		BufferedReader input = new BufferedReader(new InputStreamReader(
				System.in));

		while (true) {

			readLine = input.readLine();

			// 入力が終了した場合にループ処理を終了させる。
			if (readLine.equals("")) {
				break;
			}

			// 受け取った一行をカンマ毎に区切る
			inputString = readLine.split(",", 0);

			switch (Integer.parseInt(inputString[2])) {
			case 1:
				smallInk(Integer.parseInt(inputString[0]),
						Integer.parseInt(inputString[1]));
				break;
			case 2:
				mediumInk(Integer.parseInt(inputString[0]),
						Integer.parseInt(inputString[1]));
				break;
			case 3:
				largeInk(Integer.parseInt(inputString[0]),
						Integer.parseInt(inputString[1]));
				break;
			}
		}

		for (int i = 0; i < 10; i++) {
			for (int j = 0; j < 10; j++) {
				if (square[i][j] == 0) {
					whiteTiles++;
				} else {
					if (square[i][j] > mostDeep) {
						mostDeep = square[i][j];
					}
				}
			}
		}
		System.out.println(whiteTiles + "\r\n" + mostDeep);
	}

	/**
	 * インクが小の場合の処理
	 *
	 * @param x
	 * @param y
	 */
	static void smallInk(int x, int y) {

		square[x][y - 1]++;
		square[x - 1][y]++;
		square[x][y]++;
		square[x + 1][y]++;
		square[x][y + 1]++;
	}

	/**
	 * インクが中の場合の処理
	 *
	 * @param x
	 * @param y
	 */
	static void mediumInk(int x, int y) {

		square[x - 1][y - 1]++;
		square[x][y - 1]++;
		square[x + 1][y - 1]++;
		square[x - 1][y]++;
		square[x][y]++;
		square[x + 1][y]++;
		square[x - 1][y + 1]++;
		square[x][y + 1]++;
		square[x + 1][y + 1]++;
	}

	/**
	 * インクが大の場合の処理
	 *
	 * @param x
	 * @param y
	 */
	static void largeInk(int x, int y) {

		square[x][y - 2]++;
		square[x - 1][y - 1]++;
		square[x][y - 1]++;
		square[x + 1][y - 1]++;
		square[x - 2][y]++;
		square[x - 1][y]++;
		square[x][y]++;
		square[x + 1][y]++;
		square[x + 2][y]++;
		square[x - 1][y + 1]++;
		square[x][y + 1]++;
		square[x + 1][y + 1]++;
		square[x][y + 2]++;
	}
}

import java.util.Arrays;
