		}
		if ((y - 1) >= 0) {
			math[x][y - 1]++;
		}
		if (MATH_LENGTH > (y + 1)) {
			math[x][y + 1]++;
		}
		if (MATH_LENGTH > (x + 1)) {
			math[x + 1][y]++;
			if ((y - 1) >= 0) {
				math[x + 1][y - 1]++;
			}
			if (MATH_LENGTH > (y + 1)) {
				math[x + 1][y + 1]++;
			}
		}
	}
}

