					Map[x+1][y]++;
				}
				if(judge(x, y+1)){
					Map[x][y+1]++;
				}
			}
			if(scale >= 2){
				if(judge(x-1, y-1)){
					Map[x-1][y-1]++;
				}
				if(judge(x+1, y+1)){
					Map[x+1][y+1]++;
				}
				if(judge(x+1, y-1)){
					Map[x+1][y+1]++;
				}
				if(judge(x-1, y+1)){
					Map[x+1][y+1]++;
				}
			}
