	boolean ok(int x, int y){
		return x>=0 && y>=0 && x<10 && y<10;
	}

	public Main(){
		Scanner cin = new Scanner(System.in);
		int num[][] = new int[10][10];
		while(cin.hasNext()){
			String s = cin.nextLine();
