			if(size==3) size=4;
			for(int i=-2;i<=2;i++){
				for(int j=-2;j<=2;j++){
					if(i*i+j*j>size) continue;
					int nextx = x + i;
					int nexty = y + j;
					if(ok(nextx,nexty)) num[nextx][nexty]++;
				}
			}
		}
