        	for(int i = 0;i < size[t];i++){
        		if(x+dx[i] >= 0 && x+dx[i] < s && y+dy[i] >= 0 && y+dy[i] < s){
        			map[y+dy[i]][x+dx[i]] += 1;
        		}
        	}
        }
        int max = 0;
