import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.io.IOException;

/**
 * Dropping Ink
 */
public class Main{
    /** 紙の１辺のサイズ */
    private static final int PAPER_SIZE = 10;

    /** 小サイズのインキ滴の１辺のサイズ */
    private static final int SMALL_INK_DROP_SIZE = 3;
    /** 小サイズのインキ滴の色の着く範囲 */
    private static final boolean[] SMALL_INK_DROP =
        {
           false, true, false,
           true , true, true,
           false, true, false
        };

    /** 中サイズのインキ滴の１辺のサイズ */
    private static final int MEDIUN_INK_DROP_SIZE = 3;
    /** 中サイズのインキ滴の色の着く範囲 */
    private static final boolean[] MEDIUM_INK_DROP =
        {
           true, true, true,
           true, true, true,
           true, true, true
        };

    /** 大サイズのインキ滴の１辺のサイズ */
    private static final int LARGE_INK_DROP_SIZE = 5;
    /** 大サイズのインキ滴の色の着く範囲 */
    private static final boolean[] LARGE_INK_DROP =
        {
           false, false, true, false, false,
           false, true,  true, true,  false,
           true,  true,  true, true,  true,
           false, true,  true, true,  false,
           false, false, true, false, false
        };

    /** 紙を表す配列 */
    private static int[] paper= new int[PAPER_SIZE * PAPER_SIZE];
    /** 色の着いたマスの数 */
    private static int droppedCount = 0;
    /** 色の濃さの最大値 */
    private static int maxDropping = 0;

    /**
     * エントリポイント
     */
    public static void main(String args[]) throws IOException {        

        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String input = "";

        while(true) {
            input = br.readLine();
            /** 最後まで読みこんだら終了 */
            if(input == null) break;

            /** 入力の１行を読み込み */
            String[] splittedStr = input.split(",");
            int x = Integer.parseInt(splittedStr[0]);
            int y = Integer.parseInt(splittedStr[1]);
            int dropSize = Integer.parseInt(splittedStr[2]);

            /** インクを落とす */
            dropInk(x, y, dropSize);
        }

        /** 結果表示 */
        System.out.println(PAPER_SIZE * PAPER_SIZE - droppedCount);
        System.out.println(maxDropping);
    }

    /**
     * インクを落として紙に色を塗る
     * 入力のインクサイズに合わせて、落とす対象のインクの定数を切り替え
     */
    public static void dropInk(int x, int y, int inkSize) {
        switch(inkSize) {
        case 1:
            dropInk(x, y, SMALL_INK_DROP_SIZE, SMALL_INK_DROP);
            break;
        case 2:
            dropInk(x, y, MEDIUN_INK_DROP_SIZE, MEDIUM_INK_DROP);
            break;
        case 3:
            dropInk(x, y, LARGE_INK_DROP_SIZE, LARGE_INK_DROP);
            break;
        }
    }

    /** 
     * インクを落として紙に色を塗る
     * 引数で渡されたインキ滴について、処理を実施する。
     */
    private static void dropInk(int x, int y, int dropSize, boolean[] drop) {
        for(int i = 0; i < dropSize; i++) {
            // 色を塗る判定を実施する位置（X軸）
            int tx = x - dropSize / 2 + i;

            // 紙の外になる場合、処理をスキップ
            if(tx < 0) continue;
            if(tx >= PAPER_SIZE) break;

            for(int j = 0; j < dropSize; j++) {
                // インク的を塗るマスでなければ処理をスキップ
                if(!drop[i * dropSize + j]) continue;

                // 色を塗る判定を実施する位置（Y軸）
                int ty = y - dropSize /2 + j;

                // 紙の外になる場合、処理をスキップ
                if(ty < 0) continue;
                if(ty >= PAPER_SIZE) break;

                // 色が塗られているマスの数をカウント
                if(paper[tx * PAPER_SIZE + ty]++ == 0 ) {
                    droppedCount++;
                }
                // 最も濃い色の濃度を更新
                if(maxDropping < paper[tx * PAPER_SIZE + ty]) {
                    maxDropping = paper[tx * PAPER_SIZE + ty];
                }
            }
        }
    }
}

public class Main {

	private int[][] cells;

	public Main() {
		// 10×10 マス領域を用意
		this.cells = new int[10][10];
	}

	public static void main(String[] args) throws Exception {
		// 入力を行ごとに分割する

		Main droppingInk = new Main();
		// 行数分、配列へ格納する
		for (String inks : args) {

			// 1回落下分のインク情報
			String[] ink = inks.split(",");
			// インクのx座標
			int ink_X = Integer.parseInt(ink[0]);
			// インクのy座標
			int ink_Y = Integer.parseInt(ink[1]);
			// インクの大きさ
			int ink_size = Integer.parseInt(ink[2]);

			// インキ滴が落したマスに値を加算する
			droppingInk.carcDroppingInk(ink_X, ink_Y, ink_size);
		}

		// 結果を出力する
		droppingInk.print();

	}

	/**
	 * 方眼紙にインクを滴下する。
	 *
	 * @param ink_X
	 *            インクが滴下されるx位置
	 * @param ink_Y
	 *            インクが滴下されるy位置
	 * @param size
	 *            インクの大きさ
	 */
	private void carcDroppingInk(int ink_X, int ink_Y, int size) {
		switch (size) {
		case 3:
			isRangeAdd(ink_X - 2, ink_Y);
			isRangeAdd(ink_X, ink_Y - 2);
			isRangeAdd(ink_X + 2, ink_Y);
			isRangeAdd(ink_X, ink_Y + 2);

		case 2:
			isRangeAdd(ink_X - 1, ink_Y - 1);
			isRangeAdd(ink_X - 1, ink_Y + 1);
			isRangeAdd(ink_X + 1, ink_Y - 1);
			isRangeAdd(ink_X + 1, ink_Y + 1);

		case 1:
			isRangeAdd(ink_X, ink_Y);
			isRangeAdd(ink_X - 1, ink_Y);
			isRangeAdd(ink_X, ink_Y - 1);
			isRangeAdd(ink_X + 1, ink_Y);
			isRangeAdd(ink_X, ink_Y + 1);
		}
	}

	/**
	 * インクの滴下先が方眼紙の範囲内か判定する。 範囲内の場合、方眼紙に１追加する。
	 *
	 * @param cells_X
	 *            X座標
	 * @param cells_Y
	 *            Y座標
	 */
	private void isRangeAdd(int cells_X, int cells_Y) {
		if ((0 <= cells_X && cells_X < 10) && (0 <= cells_Y && cells_Y < 10)) {
			// インクの滴下先が方眼紙の範囲内の場合、１をセルに追加
			this.cells[cells_X][cells_Y] += 1;
		}
		// インクの滴下先が方眼紙の範囲外の場合何もしない
	}

	/**
	 * 色のついていない部分のマス目の個数、１番濃いマス目の濃さを出力する。
	 */
	private void print() {
		int countZero = 0;
		int maxInk = 0;

		for (int[] cells_x : this.cells) {
			for (int cell_xy : cells_x) {
				// 色のついていない部分のマス目の個数を計算
				if (cell_xy == 0) {
					countZero++;
				}
				// １番濃いマス目の濃さを計算
				if (maxInk < cell_xy) {
					maxInk = cell_xy;
				}
			}
		}

		// 出力
		System.out.print(countZero);
		System.out.println();
		System.out.print(maxInk);

	}
}

