import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {

	public static void main(String[] args) throws IOException {
		// 10*10マス目の紙
		int paper[][] = new int[10][10];
		// 色がついてないマス目の個数
		int emptySpace = 0;
		// 1番濃いマス目の濃さ
		int max = 0;

		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		String str = br.readLine();

		while (str != null) {
			// 文字列を指定した区切り文字(",")によって分割し,それぞれの配列の要素として格納する
			String[] array = str.split(",");
			int x = Integer.parseInt(array[0]);
			int y = Integer.parseInt(array[1]);
			int size = Integer.parseInt(array[2]);

			drop(paper, x, y, size);
		}

		// 色のついてない部分のマス目の個数と、1番濃いマス目の濃さを格納
		for (int i = 0; i < 10; i++) {
			for (int j = 0; j < 10; j++) {
				if (paper[i][j] == 0) {
					emptySpace++;
				}else if (paper[i][j] > max) {
					max = paper[i][j];
				}
			}
		}

		System.out.println(emptySpace);
		System.out.println(max);
	}

	// ぞれぞれのサイズに合わせた滴下メソッド呼び出す
	private static void drop(int paper[][], int x, int y, int size) {
		if (size == 1) {
			drop1(paper, x, y);
		}
		if (size == 2) {
			drop2(paper, x, y);
		}
		if (size == 3) {
			drop3(paper, x, y);
		}
	}

	// サイズ小の滴下メソッド
	private static void drop1(int paper[][], int x, int y) {
		paper[x][y]++;
		if (x + 1 < 10) {
			paper[x + 1][y]++;
		}
		if (x - 1 > -1) {
			paper[x - 1][y]++;
		}
		if (y + 1 < 10) {
			paper[x][y + 1]++;
		}
		if (y - 1 > -1) {
			paper[x][y - 1]++;
		}
	}

	// サイズ中の滴下メソッド
	private static void drop2(int paper[][], int x, int y) {
		drop1(paper, x, y);
		if (x + 1 < 10 && y + 1 < 10) {
			paper[x + 1][y + 1]++;
		}
		if (x + 1 < 10 && y - 1 > -1) {
			paper[x + 1][y - 1]++;
		}
		if (x - 1 > -1 && y + 1 < 10) {
			paper[x - 1][y + 1]++;
		}
		if (x - 1 > -1 && y - 1 > -1) {
			paper[x - 1][y - 1]++;
		}
	}

	// サイズ大の滴下メソッド
	private static void drop3(int paper[][], int x, int y) {
		drop2(paper, x, y);
		if (x + 2 < 10) {
			paper[x + 2][y]++;
		}
		if (x - 2 > -1) {
			paper[x - 2][y]++;
		}
		if (y + 2 < 10) {
			paper[x][y + 2]++;
		}
		if (y - 2 > -1) {
			paper[x][y - 2]++;
		}
	}
}

import java.util.Scanner;

public class Main {
	public static void main(String[] args){
		Scanner stdIn = new Scanner(System.in);
		int[][] square = new int[14][14];
		int[] func1_x = {1,0,-1,0};
		int[] func1_y = {0,1,0,-1};
		int[] func2_x = {1,1,0,-1,-1,-1,0,1};
		int[] func2_y = {0,1,1,1,0,-1,-1,-1};
		int[] func3_x = {2,0,-2,0};
		int[] func3_y = {0,2,0,-2};

		while(stdIn.hasNext()){
			String str = stdIn.nextLine();
			int x = (int)str.charAt(0)-(int)'0'+2;
			int y = (int)str.charAt(2)-(int)'0'+2;
			square[x][y]++;
			if((int)str.charAt(4)==1){
				for(int i=0;i<4;i++){
					square[x+func1_x[i]][y+func1_y[i]]++;
				}
			}
			else if((int)str.charAt(4)==2){
				for(int i=0;i<8;i++){
					square[x+func2_x[i]][y+func2_y[i]]++;
				}
			}
			else if((int)str.charAt(4)==3){
				for(int i=0;i<8;i++){
					square[x+func2_x[i]][y+func2_y[i]]++;
				}
				for(int i=0;i<4;i++){
					square[x+func3_x[i]][y+func3_y[i]]++;
				}
			}
		}
		int count = 0;
		int depth = 0;
		for(int i=2;i<=11;i++){
			for(int j=2;j<=11;j++){
				if(square[i][j]==0){
					count++;
				}
				if(square[i][j]>depth){
					depth = square[i][j];
				}
			}
		}

		System.out.println(count);
		System.out.println(depth);
	}
}

import java.io.BufferedReader;
import java.io.InputStreamReader;

public class Main {
	private final static byte MASK_RIGHT = 0x0F; //右4bit数値化用マスク
	private final static int STRIP_LENGTH = 10; //短冊1行の長さ
	private static int[] punchTape = new int[100]; //10*10の方眼紙を短冊にしてくっつけた紙テープ

	public static void main(String[] args) throws Exception {
		BufferedReader reader = new BufferedReader(new InputStreamReader(System.in), 512);
		String sLine;
		while ((sLine = reader.readLine()) != null) {
			droppingInk(sLine.charAt(0) & MASK_RIGHT, sLine.charAt(2) & MASK_RIGHT, sLine.charAt(4) & MASK_RIGHT);
		}
		calcResult();
	}

	/**
	 *都合の良い滴下
	 */
	private static void droppingInk(int x, int y, int size) {
		sinking(x, y - 1);
		sinking(x - 1, y);
		sinking(x, y);
		sinking(x + 1, y);
		sinking(x, y + 1);
		if (size == 1) {
			return;
		}

		sinking(x - 1, y - 1);
		sinking(x + 1, y - 1);
		sinking(x - 1, y + 1);
		sinking(x + 1, y + 1);
		if (size == 2) {
			return;
		}

		sinking(x, y - 2);
		sinking(x - 2, y);
		sinking(x + 2, y);
		sinking(x, y + 2);
	}

	/**
	 * 床は汚さないようにする
	 */
	private static void sinking(int x, int y) {
		if (x >= 0 && x < 10 && y >= 0 && y < 10) {
			punchTape[x * STRIP_LENGTH + y]++;
		}
	}

	/**
	 *紙の染みを数え、損害を報告する
	 */
	private static void calcResult() {
		int white = 0;//綺麗な部分
		int max = 0;//一番ひどい汚れ
		for (int cell : punchTape) {
			if (cell == 0) {
				white++;
			} else if (cell > max) {
				max = cell;
			}
		}
		System.out.println(white);
		System.out.println(max);
	}
}

package volume0;

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {

	private static int[][] square = new int[10][10];
	private static int mostDeep = 0;
	private static int whiteTiles = 0;

	/**
	 * 標準入力された値を受け取り、インクのサイズによって滲む範囲を切り分ける。
	 * すべての入力が終了した後にまだインクが滲んでいないマスの数と、最も濃いマスを判定し、出力する。
	 *
	 * @param args
	 * @throws IOException
	 */
	public static void main(String[] args) throws IOException {

		String[] inputString;
		String readLine = "";

		BufferedReader input = new BufferedReader(new InputStreamReader(
				System.in));

		while (true) {

			readLine = input.readLine();

			// 入力が終了した場合にループ処理を終了させる。
			if (readLine.equals("")) {
				break;
			}

			// 受け取った一行をカンマ毎に区切る
			inputString = readLine.split(",", 0);

			switch (Integer.parseInt(inputString[2])) {
			case 1:
				smallInk(Integer.parseInt(inputString[0]),
						Integer.parseInt(inputString[1]));
				break;
			case 2:
				mediumInk(Integer.parseInt(inputString[0]),
						Integer.parseInt(inputString[1]));
				break;
			case 3:
				largeInk(Integer.parseInt(inputString[0]),
						Integer.parseInt(inputString[1]));
				break;
			}
		}

		for (int i = 0; i < 10; i++) {
			for (int j = 0; j < 10; j++) {
				if (square[i][j] == 0) {
					whiteTiles++;
				} else {
					if (square[i][j] > mostDeep) {
						mostDeep = square[i][j];
					}
				}
			}
		}
		System.out.println(whiteTiles + "\r\n" + mostDeep);
	}

	/**
	 * インクが小の場合の処理
	 *
	 * @param x
	 * @param y
	 */
	static void smallInk(int x, int y) {

		if (x - 1 >= 0) {
			square[x - 1][y]++;
		}

		if (x + 1 <= 9) {
			square[x + 1][y]++;
		}

		square[x][y]++;

		if (y - 1 >= 0) {
			square[x][y - 1]++;
		}

		if (y + 1 <= 9) {
			square[x][y + 1]++;
		}

	}

	/**
	 * インクが中の場合の処理
	 *
	 * @param x
	 * @param y
	 */
	static void mediumInk(int x, int y) {

		for (int i = x - 1; i <= x + 1; i++) {
			for (int j = y - 1; j <= y + 1; j++) {
				if (x >= 0 && x <= 10) {
					square[i][j]++;
				}
			}
		}
	}

	/**
	 * インクが大の場合の処理
	 *
	 * @param x
	 * @param y
	 */
	static void largeInk(int x, int y) {

		if (x - 2 >= 0) {
			square[x - 2][y]++;
		}

		if (x + 2 <= 9) {
			square[x + 2][y]++;
		}

		for (int i = x - 1; i <= x + 1; i++) {
			for (int j = y - 1; j <= y + 1; j++) {
				if (x >= 0 && x <= 10) {
					square[i][j]++;
				}
			}
		}

		if (y - 2 >= 0) {
			square[x][y - 2]++;
		}

		if (y + 2 <= 9) {
			square[x][y + 2]++;
		}
	}
}

