import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {

	/** 「紙」を配列として定義 */
	private static int[][] paper = new int[10][10];

	/** インクのサイズ：小を表す数字 */
	private final static String INK_SIZE_S = "1";

	/** インクのサイズ：中を表す数字 */
	private final static String INK_SIZE_M = "2";

	/** インクのサイズ：大を表す数字 */
	private final static String INK_SIZE_L = "3";

	/** 色のついていない部分のマス目の個数：初期値100 */
	private static int plainCells = 100;

	/** 一番濃いマス目の濃さ */
	private static int maximumValue = 0;

	/**
	 *
	 *
	 * @param args
	 *            利用しない。
	 * @throws IOException
	 */
	public static void main(String[] args) throws IOException {

		// 入力値を受け取る
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));

		// インクを垂らす
		String line = null;
		while ((line = br.readLine()) != null) {

			String[] inkInfo = line.split(",");
			int x = Integer.parseInt(inkInfo[0]);
			int y = Integer.parseInt(inkInfo[1]);
			String size = inkInfo[2];

			// 小のサイズのインクの濃さを加算
			dropInk(x, y); // 真ん中
			dropInk(x - 1, y); // 左
			dropInk(x + 1, y); // 右
			dropInk(x, y - 1); // 下
			dropInk(x, y + 1); // 上

			if (INK_SIZE_S.equals(size)) {
				continue;
			}

			// 中のサイズのインクの濃さを加算
			dropInk(x - 1, y - 1); // 左下
			dropInk(x - 1, y + 1); // 左上
			dropInk(x + 1, y - 1); // 右下
			dropInk(x + 1, y + 1); // 右上

			if (INK_SIZE_M.equals(size)) {
				continue;
			}

			// 大のサイズのインクの濃さを加算
			dropInk(x - 2, y); // 2つ左
			dropInk(x + 2, y); // 2つ右
			dropInk(x, y - 2); // 2つ下
			dropInk(x, y + 2); // 2つ上
		}

		// 結果を出力
		System.out.println(plainCells); // 色のついていない部分のマス目の個数
		System.out.println(maximumValue); // １番濃いマス目の濃さ
	}

	/**
	 * <p>
	 * 引数で指定されたマス目にインクを落とせるか（「紙」の大きさの範囲内か）を確認し、 落とせる場合はマス目の濃さを+1する。</br>
	 * この際、インクを落としたマス目の濃さが0であれば「色のついていない部分のマス目の個数」から1を引く。<br/>
	 * 「一番濃いマス目の濃さ」よりも濃くなるのであれば濃さを更新する。<br/>
	 * </p>
	 *
	 * @param x
	 *            インクを落とすマス目のx座標
	 * @param y
	 *            インクを落とすマス目のy座標
	 */
	private static void dropInk(int x, int y) {

		if (0 <= x && x < 10 && 0 <= y && y < 10) {
			if (paper[x][y] == 0) {
				plainCells -= 1;
			}

			paper[x][y] += 1;

			if (paper[x][y] > maximumValue) {
				maximumValue = paper[x][y];
			}

		}
	}

}

import java.util.*;
import java.io.*;

class Main {

    static final int SMALL = 1;
    static final int MIDDLE = 2;
    static final int LARGE = 3;

    public static void main( String[] args ) {

	final Scanner stdin = new Scanner( System.in );

	int[][] map = new int[10][10];

	while ( stdin.hasNextLine() ) {
	    String[] input = stdin.nextLine().split(",");
	    final int x = Integer.parseInt( input[0] );
	    final int y = Integer.parseInt( input[1] );
	    final int size = Integer.parseInt( input[2] );
	    switch ( size ) {
	    case SMALL:
		dropSmall( map, x, y );
		break;
	    case MIDDLE:
		dropMiddle( map, x, y );
		break;
	    case LARGE:
		dropLarge( map, x, y );
		break;
	    }
	}

	final int noOfZero = countZero( map );
	final int max = countMax( map );
	System.out.println(noOfZero);
	System.out.println(max);

    }

    static int countZero( int[][] map ) {
	int count = 0;
	for ( int i = 0; i < 10; i++ ) {
	    for ( int j = 0; j < 10; j++ ) {
		if ( map[i][j] == 0 ) {
		    count++;
		}
	    }
	}
	return count;
    }

    static int countMax( int[][] map ) {
	int max = 0;
	for ( int i = 0; i < 10; i++ ) {
	    for ( int j = 0; j < 10; j++ ) {
		if ( map[i][j] > max ) {
		    max = map[i][j];
		}
	    }
	}
	return max;
    }

    static boolean check( int val ) {
	return 0 <= val && val <= 9;
    }

    static void dropSmall( int[][] map, int x, int y ) {
	final int[] width = {0, 1, 0};
	final int[] yindex = {-1, 0, 1};

	for ( int i = 0; i < 3; i++ ) {
	    final int cy = y + yindex[i];
	    for ( int j = x - width[i]; j <= x + width[i]; j++ ) {
		if ( check( cy ) && check( j ) ) {
		    map[cy][j]++;
		}
	    }		
	}

    }

    static void dropMiddle( int[][] map, int x, int y ) {
	final int xs = Math.max( 0, x - 1 );
	final int xe = Math.min( 9, x + 1 );
	final int ys = Math.max( 0, y - 1 );
	final int ye = Math.min( 9, y + 1 );

	for ( int i = ys; i <= ye; i++ ) {
	    for ( int j = xs; j <= xe; j++ ) {
		map[i][j]++;
	    }
	}
    }

    static void dropLarge( int[][] map, int x, int y ) {
	final int[] width = {0, 1, 2, 1, 0};
	final int[] yindex = {-2, -1, 0, 1, 2};

	for ( int i = 0; i < 5; i++ ) {
	    final int cy = y + yindex[i];
	    for ( int j = x - width[i]; j <= x + width[i]; j++ ) {
		if ( check( cy ) && check( j ) ) {
		    map[cy][j]++;
		}
	    }		
	}
    }

}

