import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.Arrays;

public class Main {

    //行数
    static final int n = 10;

    //列数
    static final int m = 10;

    //インキ滴（小）
    static final String INK_SIZE_SMALL = "1";

    //インキ滴（中） 
    static final String INK_SIZE_MEDIUM = "2";

    //インキ滴（大）
    static final String INK_SIZE_LARGE = "3";

    public static void main(String[] args) throws IOException {

        //紙
        int[][] paper = new int[n][m];

        //標準出力より、「落とすインキ滴」の x座標, y座標, サイズを取得する。
        BufferedReader bufferedReader = new BufferedReader(
                new InputStreamReader(System.in), 1);

        String line;
        while ((line = bufferedReader.readLine()) != null) {
            //「落とすインキ滴」のサイズを取得
            String inkSize = line.split(",")[2];

            //「落とすインキ滴」のx座標を取得
            int x = Integer.parseInt(line.split(",")[0]);

            //「落とすインキ滴」のy座標を取得
            int y = Integer.parseInt(line.split(",")[1]);

            //インキ滴のサイズによって演算処理を選択
            if (INK_SIZE_SMALL.equals(inkSize)) {
                paper = smallInk(x, y, paper);
            } else if (INK_SIZE_MEDIUM.equals(inkSize)) {
                paper = mediumInk(x, y, paper);
            } else if (INK_SIZE_LARGE.equals(inkSize)) {
                paper = largeInk(x, y, paper);
            }
        }

        //色がついていないマス数
        int colorless = 0;

        //色の濃さ
        int depth = 0;

        //色がついていないマス数、および色の濃さ（最大）の計算
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                if (paper[i][j] == 0) {
                    colorless++;
                }
                if (depth < paper[i][j]) {
                    depth = paper[i][j];
                }
            }
        }
        //結果の出力
        System.out.println(colorless);
        System.out.println(depth);
    }

    //インキ滴（小）の演算メソッド
    private static int[][] smallInk(int x, int y, int[][] array) {
        if (x - 1 >= 0) {
            array[y][x - 1]++;
        }

        if (y - 1 >= 0) {
            array[y - 1][x]++;
        }
        array[y][x]++;
        if (y + 1 < n) {
            array[y + 1][x]++;
        }

        if (x + 1 < m) {
            array[y][x + 1]++;
        }
        return array;
    }

    //インキ滴（中）の演算メソッド
    private static int[][] mediumInk(int x, int y, int[][] array) {
        //インキ滴（小）の演算結果の取得
        array = smallInk(x, y, array);

        //インキ滴（中）分の範囲拡大
        if (y - 1 >= 0) {
            if (x - 1 >= 0)
                array[y - 1][x - 1]++;
            if (x + 1 < m)
                array[y - 1][x + 1]++;
        }
        if (y + 1 < n) {
            if (x - 1 >= 0)
                array[y + 1][x - 1]++;
            if (x + 1 < m)
                array[y + 1][x + 1]++;
        }

        return array;
    }

    //インキ滴（大）の演算メソッド
    private static int[][] largeInk(int x, int y, int[][] array) {
        //インキ滴（中）の演算結果の取得
        array = mediumInk(x, y, array);

        //インキ滴（大）分の範囲拡大
        if (x - 2 >= 0) {
            array[y][x - 2]++;
        }

        if (y - 2 >= 0) {
            array[y - 2][x]++;
        }
        if (y + 2 < n) {
            array[y + 2][x]++;
        }

        if (x + 2 < m) {
            array[y][x + 2]++;
        }

        return array;
    }

}

import java.util.ArrayList;
import java.util.List;
import java.util.Scanner;


class Main {

	public static void main(String... args) {
		Scanner scanner = null;
		try {
			scanner = new Scanner(System.in);

			Paper paper = new Paper();
			while (scanner.hasNext()) {
				String line = scanner.nextLine();
				String[] lines = line.split(",");
				paper.drop(Integer.parseInt(lines[0]),
						Integer.parseInt(lines[1]), Integer.parseInt(lines[2]));
			}
			paper.printOutput();

		} finally {
			if (scanner != null) {
				scanner.close();
			}
		}
	}

	static class Paper {
		private List<List<Integer>> X_LIST = new ArrayList<List<Integer>>();
		Paper() {
			for (int i = 0; i < 10; i++) {
				List<Integer> yList = new ArrayList<Integer>();
				for (int j  = 0; j < 10; j++) {
					yList.add(0);
				}
				X_LIST.add(yList);
			}
		}

		void drop(int x, int y, int size) {
			dropCell(x, y);

			switch (size) {
			case 3:
				dropCell(x + 2, y);
				dropCell(x - 2, y);
				dropCell(x, y + 2);
				dropCell(x, y - 2);
			case 2:
				dropCell(x + 1, y + 1);
				dropCell(x + 1, y - 1);
				dropCell(x - 1, y + 1);
				dropCell(x - 1, y - 1);
			case 1:
				dropCell(x + 1, y);
				dropCell(x - 1, y);
				dropCell(x, y + 1);
				dropCell(x, y - 1);
			default:
				break;
			}
		}

		void dropCell(int x, int y) {
			if (x < 0 || y < 0 || 9 < x || 9 < y) {
				return;
			}
			List<Integer> yList = X_LIST.get(x);
			yList.set(y, yList.get(y) + 1);
		}

		void printOutput() {
			int whiteNum = 0;
			int maxDensity = 0;
			for (List<Integer> yList : X_LIST) {
				for (Integer i : yList) {
					whiteNum = (0 < i) ? whiteNum : whiteNum + 1;
					maxDensity = (maxDensity < i) ? i : maxDensity;
				}
			}
			System.out.println(whiteNum);
			System.out.println(maxDensity);
		}
	}
}

import java.io.IOException;
import java.io.BufferedReader;
import java.io.InputStreamReader;

public class Main
{
    public static void main(String[] arg) throws IOException
    {
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		String s;
		int[][] paper = new int[14][14];
		// assume we use 14*14 cells, then finally ignore the 0 to 1 and 12 to 13
		int count=0, max=0;

		while(true)
		{
			if((s = br.readLine()).isEmpty()) break;
			int x = Integer.parseInt(s.split(",")[0]);
			int y = Integer.parseInt(s.split(",")[1]);
			int size = Integer.parseInt(s.split(",")[2]);

			drop(x+2,y+2,size,paper); // as we use 14*14 paper(but just assumes it as 10*10)
		}
		for(int i=2; i<12; i++)
			for(int j=2; j<12; j++){
				if(paper[i][j]==0) count++;
				if(paper[i][j] > max) max = paper[i][j];
			}
		System.out.println(count);
		System.out.println(max);
	}

	private static void drop(int x, int y, int size, int[][] paper)
	{
		if(size==1){ 
			for(int i=x-1; i<x+2; i++) paper[y][i]++;
			for(int i=y-1; i<y+2; i++) paper[i][x]++;
			paper[y][x]--;
		} else if(size==2) {
			for(int i=x-1; i<x+2; i++)
				for(int j=y-1; j<y+2; j++)
					paper[j][i]++;
		} else {
			for(int i=x-2; i<x+3; i++) paper[y][i]++;
			for(int i=x-1; i<x+2; i++) {
				paper[y+1][i]++;
				paper[y-1][i]++;
			}
			for(int i=y-2; i<y+3; i++) paper[i][x]++;
			paper[y+1][x]--;
			paper[y-1][x]--;
			paper[y][x]--;
		}
	}
}

import java.util.*;

public class Main {

	static int zeroCheck(int[][] p){
		int count=0;
		for(int i[]: p)
			for(int j : i) if(j==0) count++;
		return count;
	}

	static int max(int[][] p){
		int max=0;
		for(int i[] : p)
			for(int j:i) if(max<j) max = j ;
		return max;
	}

	static boolean judge(int x,int y){
		if(x>=0 && x<=9 && y>=0 && y<=9)
			return true;
		else return false;
	}

	static int[][] dropS(int[][] p,int x,int y){
		if(judge(x,y)) p[y][x]++;
		if(judge(x+1,y)) p[y][x+1] ++;
		if(judge(x-1,y)) p[y][x-1] ++;
		if(judge(x,y+1)) p[y+1][x] ++;
		if(judge(x,y-1)) p[y-1][x] ++;
		return p;
	}

	static int[][] dropM(int[][] p,int x,int y){
		for(int i=-1; i<=1; i++){
			for(int j=-1; j<=1; j++){
				if(judge(x+i,y+j)) p[y+j][x+i]++;
			}
		}
		return p;
	}

	static int[][] dropL(int[][] p,int x,int y){
		p = dropM(p,x,y);
		if(judge(x+2,y)) p[y][x+2] ++;
		if(judge(x-2,y)) p[y][x-2] ++;
		if(judge(x,y+2)) p[y+2][x] ++;
		if(judge(x,y-2)) p[y-2][x] ++;
		return p;
	}

	public static String[] kommmaReader() {
		Scanner sc = new Scanner(System.in);
		String line;
		line=sc.nextLine();
		String[] array = line.split(",");
		return array;
	}

