				for(int i = -1; i <= 1; i++) {
					if(n[0] + i < 0) continue;
					for(int j = -1; j <= 1; j++) {
						if(n[1] + j < 0) continue;
						board[n[0] + i][n[1] + j]++;
					}
				}
			} else {
				for(int i = 0; i < 10; i++) {
					for(int j = 0; j < 10; j++) {
						if(n[2] == 1) {
							if(Math.abs(n[0] - i) + Math.abs(n[1] - j) <= 1) {
								board[i][j]++;
							}
						} else {
							if(Math.abs(n[0] - i) + Math.abs(n[1] - j) <= 2) {
								board[i][j]++;
							}
						}
					}
				}
			}
		}

		int cnt = 0, max = 0;
		for(int i = 0; i < 10; i++) {
