									map[x+i][y]++;
							if(check(x,y+i))
								map[x][y+i]++;
					}
				case 2:
					for(int i = -1 ; i <=1; i+=2){
						for(int j = -1 ; j <=1 ;j +=2){
							if(check(x+i,y+j))
								map[x+i][y+j]++;
						}
					}
				case 1:
					for(int i = -1; i <=1; i+=2){
						if(check(x+i ,y))
								map[x+i][y]++;
						if(check(x,y+i))
							map[x][y+i]++;
					}
					map[x][y]++;
				}
