import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {

	private static int ink[][] = new int[10][10];
	//private static String line[] = new String[3];
	private static int clearCells = 0;
	private static int mostDeepCell = 0;

	public static void main(String[] args) throws NumberFormatException,
			IOException {

		InputStreamReader isr = new InputStreamReader(System.in);
		BufferedReader br = new BufferedReader(isr);

		String str = null;
		while ((str = br.readLine()) != null) {

			String[] line = str.split(",", 3);

			int x = Integer.parseInt(line[0]);
			int y = Integer.parseInt(line[1]);
			int size = Integer.parseInt(line[2]);

			if (size == 1) {
				small(x, y);
			}

			if (size == 2) {
				medium(x, y);
			}

			if (size == 3) {
				large(x, y);
			}
			for (int i = 0; i < 10; ++i) {
				for (int j = 0; j < 10; ++j) {

					if (ink[i][j] == 0) {
						clearCells++;
					}

					if (mostDeepCell < ink[i][j]) {
						mostDeepCell = ink[i][j];
					}
				}
			}

			System.out.println(clearCells);
			System.out.println(mostDeepCell);

		}
	}

	private static void small(int x, int y) {

		ink[x][y]++;

		if (0 <= x - 1) {
			ink[x - 1][y]++;
		}

		if (x + 1 < 10) {
			ink[x + 1][y]++;
		}

		if (0 <= y - 1) {
			ink[x][y - 1]++;
		}

		if (y + 1 < 10) {
			ink[x][y + 1]++;
		}
	}

	private static void medium(int x, int y) {

		small(x, y);

		if (0 <= x - 1 && 0 <= y - 1) {
			ink[x - 1][y - 1]++;
		}

		if (0 <= x - 1 && y + 1 < 10) {
			ink[x - 1][y + 1]++;
		}

		if (x + 1 < 10 && 0 <= y - 1) {
			ink[x + 1][y - 1]++;
		}

		if (x + 1 < 10 && y + 1 < 10) {
			ink[x + 1][y + 1]++;
		}
	}

	private static void large(int x, int y) {

		medium(x, y);

		if (x + 2 < 10) {
			ink[x + 2][y]++;
		}

		if (0 <= x - 2) {
			ink[x - 2][y]++;
		}

		if (y + 2 < 10) {
			ink[x][y + 2]++;
		}

		if (0 <= y - 2) {
			ink[x][y - 2]++;
		}
	}
}

import java.util.Scanner;

public class Main {

	public static int[][] dropBig (int[][] paper, int x, int y) {
		paper = dropMid(paper,x,y);
		paper[y - 2][x] += 1;
		paper[y][x - 2] += 1;
		paper[y + 2][x] += 1;
		paper[y][x + 2] += 1;
		return paper;
	}

	public static int[][] dropMid (int[][] paper, int x, int y) {
		paper = dropSmall(paper,x,y);
		paper[y + 1][x + 1] += 1;
		paper[y + 1][x - 1] += 1;
		paper[y - 1][x + 1] += 1;
		paper[y - 1][x - 1] += 1;
		return paper;
	}

	public static int[][] dropSmall (int[][] paper, int x, int y) {
		paper[y][x] += 1;
		paper[y + 1][x] += 1;
		paper[y - 1][x] += 1;
		paper[y][x + 1] += 1;
		paper[y][x - 1] += 1;
		return paper;
	}

	public static int[][] sol (int[][] paper, String[] str) {
		int x = Integer.parseInt(str[0]) + 2;
		int y = Integer.parseInt(str[1]) + 2;
		int op = Integer.parseInt(str[2]);
		switch (op) {
			case 1 : paper = dropSmall(paper,x,y); break;
			case 2 : paper = dropMid(paper,x,y); break;
			case 3 : paper = dropBig(paper,x,y); break;
		}
		return paper;
	}

	public static void main(String[] args) {

		Scanner sc = new Scanner(System.in);
		int size = 10;
		int[][] paper = new int[size + 4][size + 4];

		while (sc.hasNextLine()) {
			String[] str = sc.nextLine().split(",");
			paper = sol(paper, str);
		}

		int countZero = 0, max = 0;
		for (int i = 2; i < paper.length - 2; i++) {
			for (int j = 2; j < paper[i].length - 2; j++) {
				if (paper[i][j] == 0) countZero++;
				max = Math.max(max, paper[i][j]);
			}
		}

		System.out.println(countZero + "\n" + max);

	}

}

import java.io.*;
import java.util.*;

class Main {

	public static int[][] paper = new int[16][16];

	public static void small (int x, int y) {

		paper[y][x]++;
		paper[y - 1][x]++;
		paper[y + 1][x]++;
		paper[y][x - 1]++;
		paper[y][x + 1]++;

	}

	public static void middle (int x, int y) {

		small(x,y);
		paper[y - 1][x - 1]++;
		paper[y - 1][x + 1]++;
		paper[y + 1][x - 1]++;
		paper[y + 1][x + 1]++;

	}

	public static void big (int x, int y) {

		middle(x,y);
		paper[y - 2][x]++;
		paper[y + 2][x]++;
		paper[y][x - 2]++;
		paper[y][x + 2]++;

	}

	public static void main(String[] args) throws IOException {

		BufferedReader br =
			new BufferedReader(new InputStreamReader(System.in));

		while (true) {

			String s = br.readLine();
			if (s == null) break;
			StringTokenizer st = new StringTokenizer(s,",");
			int x = Integer.parseInt(st.nextToken()) + 3;
			int y = Integer.parseInt(st.nextToken()) + 3;
			int size = Integer.parseInt(st.nextToken());

			switch (size) {
				case 1 : small(x,y);  break;
				case 2 : middle(x,y); break;
				case 3 : big(x,y);    break;
			}

		}

		int max = 0;
		int cnt = 0;

		for (int i = 3; i < paper.length - 3; i++) {
			for (int j = 3; j < paper.length - 3; j++) {
				max = Math.max(max, paper[i][j]);
				if (paper[i][j] == 0) cnt++;
			}
		}
		System.out.printf("%d\n%d\n", cnt, max);
	}

}

