import java.io.BufferedReader;
import java.io.InputStreamReader;

public class Main {
	private final static byte MASK_RIGHT = 0x0F; //右4bit数値化用マスク
	private final static int STRIP_LENGTH = 10; //短冊1行の長さ
	private static int[] punchTape = new int[100]; //10*10の方眼紙を短冊にしてくっつけた紙テープ

	public static void main(String[] args) throws Exception {
		BufferedReader reader = new BufferedReader(new InputStreamReader(System.in), 512);
		String sLine;
		while ((sLine = reader.readLine()) != null) {
			droppingInk(sLine.charAt(0) & MASK_RIGHT, sLine.charAt(2) & MASK_RIGHT, sLine.charAt(4) & MASK_RIGHT);
		}
		calcResult();
	}

	/**
	 *都合の良い滴下
	 */
	private static void droppingInk(int x, int y, int size) {
		sinking(x, y - 1);
		sinking(x - 1, y);
		sinking(x, y);
		sinking(x + 1, y);
		sinking(x, y + 1);
		if (size == 1) {
			return;
		}

		sinking(x - 1, y - 1);
		sinking(x + 1, y - 1);
		sinking(x - 1, y + 1);
		sinking(x + 1, y + 1);
		if (size == 2) {
			return;
		}

		sinking(x, y - 2);
		sinking(x - 2, y);
		sinking(x + 2, y);
		sinking(x, y + 2);
	}

	/**
	 * 床は汚さないようにする
	 */
	private static void sinking(int x, int y) {
		if (x >= 0 && x < 10 && y >= 0 && y < 10) {
			punchTape[x * STRIP_LENGTH + y]++;
		}
	}

	/**
	 *紙の染みを数え、損害を報告する
	 */
	private static void calcResult() {
		int white = 0;//綺麗な部分
		int max = 0;//一番ひどい汚れ
		for (int cell : punchTape) {
			if (cell == 0) {
				white++;
			} else if (cell > max) {
				max = cell;
			}
		}
		System.out.println(white);
		System.out.println(max);
	}
}

import java.util.*;

public class Main {
	static int[][] paper = new int[10][10];
	static void draw(int a,int b){
		if(a<0||a>9||b<0||b>9) return;
		paper[a][b] += 1;
	}
	static void drawsmall(int a,int b){
		draw(a,b);draw(a,b-1);draw(a,b+1);draw(a+1,b);draw(a-1,b);
		return;
	}
	static void drawmid(int a,int b){
		drawsmall(a,b);draw(a-1,b-1);draw(a-1,b+1);draw(a+1,b-1);draw(a+1,b+1);
		return;
	}
	static void drawlarge(int a, int b){
		drawmid(a,b);draw(a-2,b);draw(a+2,b);draw(a,b-2);draw(a,b+2);
		return;
	}
	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		sc.useDelimiter(",");
		int zerocount = 0,max = 0;
		int[][] paper = new int[10][10];
		for (int i = 0; i < 10; i++) {
			for (int j = 0; j < 10; j++) {
				paper[i][j] = 0;
			}
		}
		while (sc.hasNext()) {
			int x = sc.nextInt();
			int y = sc.nextInt();
			int size = sc.nextInt();
			switch (size) {
			case 1:
				drawsmall(x,y);
				break;
			case 2:
				drawmid(x,y);
				break;
			case 3:
				drawlarge(x,y);
				break;
			}
		}
		for(int k = 0; k < 10;k++){
			for(int l = 0;l < 10;l++){
				if(paper[k][l] == 0){
					zerocount++;
				}
				if(paper[k][l] >= max){
					paper[k][l] = max;
				}
			}
		}
		System.out.printf("%d\n%d\n",zerocount,max);
	}

}

