import java.util.*;
public class Main {
	static int[][] map = new int[10][10];
	static void syou(int x,int y){
		int xhou[]={-1,0,1,0};
		int yhou[]={0,-1,0,1};
		for(int i=0;i<4;i++){
			int nx=xhou[i]+x;
			int ny=yhou[i]+y;
			if(nx<0||nx>=10||ny<0||ny>=10)continue;
			map[ny][nx]+=1;
		}
		map[y][x]+=1;
	}
	static void tyuu(int x,int y){
		for(int i=-1;i<=1;i++){
			for(int s=-1;s<=1;s++){
				int nx=x+s;
				int ny=y+i;
				if(nx<0||nx>=10||ny<0||ny>=10)continue;
				map[ny][nx]+=1;
			}
		}
	}
	static void dai(int x,int y){
		for(int i=-1;i<=1;i++){
			for(int s=-1;s<=1;s++){
				int nx=x+s;
				int ny=y+i;
				if(nx<0||nx>=10||ny<0||ny>=10)continue;
				map[ny][nx]+=1;
			}
		}
		int xhou[]={-2,0,2,0};
		int yhou[]={0,-2,0,2};
		for(int i=0;i<4;i++){
			int nx=xhou[i]+x;
			int ny=yhou[i]+y;
			if(nx<0||nx>=10||ny<0||ny>=10)continue;
			map[ny][nx]+=1;
		}
	}
	public static void main(String[] args) {
		Scanner in = new Scanner(System.in);
		for(int i=0;i<10;i++)for(int s=0;s<10;s++)map[i][s]=0;
		while(in.hasNext()){
			String text=in.next();
			int x=text.charAt(0)-'0';
			int y=text.charAt(2)-'0';
			int kosa =text.charAt(4)-'0';
			switch(kosa){
			case 1:
				syou(x,y);
				break;
			case 2:
				tyuu(x,y);
				break;
			case 3:
				dai(x,y);
				break;
			}
		}
		int cnt=0,big=0;
		for(int i=0;i<10;i++){
			for(int s=0;s<10;s++){
				if(map[i][s]==0)cnt++;
				big=Math.max(big, map[i][s]);
			}
		}
		System.out.println(cnt);
		System.out.println(big);
	}
}

import java.util.*;
import java.io.*;
import java.awt.geom.*;
import java.math.*;

public class Main {

	static final BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
	static final PrintWriter out = new PrintWriter(System.out,false);

	static void solve() throws IOException{
		int[][] table = new int[30][30];
		String s;
		while ((s = br.readLine()) != null) {
			String[] ss = s.split(",");
			int x = Integer.parseInt(ss[0]) + 10;
			int y = Integer.parseInt(ss[1]) + 10;
			int size = Integer.parseInt(ss[2]);

			switch(size) {
				case 3:
					table[x-2][y]++;
					table[x][y-2]++;
					table[x+2][y]++;
					table[x][y+2]++;
				case 2:
					table[x-1][y-1]++;
					table[x+1][y-1]++;
					table[x-1][y+1]++;
					table[x+1][y+1]++;
				case 1:
					table[x][y]++;
					table[x-1][y]++;
					table[x][y-1]++;
					table[x+1][y]++;
					table[x][y+1]++;
			}
		}

		int cnt = 0, max = 0;

		for (int i=10; i<20; i++) {
			for (int j=10; j<20; j++) {
				if (table[i][j] == 0) cnt++;
				else max = Math.max(max, table[i][j]);
			}
		}

		out.println(cnt);
		out.println(max);
	}

	public static void main(String[] args) throws Exception {
		long start = System.currentTimeMillis();

		solve();
		out.flush();

		long end = System.currentTimeMillis();
		//trace(end-start + "ms");
		br.close();
		out.close();
	}

	static void trace(Object... o) { System.out.println(Arrays.deepToString(o));}
}

