				}
				if (max < cells[x][y]) {
					max = cells[x][y];
				}
			}
		}
		System.out.println(blind + "\n" + max);
	}

	private static void set(int x, int y, int size) {
		cells[x][y]++;
		cells[x - 1][y]++;
		cells[x + 1][y]++;
		cells[x][y - 1]++;
		cells[x][y + 1]++;
		if (size >= 2) {
			cells[x - 1][y - 1]++;
			cells[x - 1][y + 1]++;
			cells[x + 1][y - 1]++;
			cells[x + 1][y + 1]++;
		}
		if (size >= 3) {
			cells[x - 2][y]++;
			cells[x + 2][y]++;
			cells[x][y - 2]++;
			cells[x][y + 2]++;
		}
	}
}

