public class Main {	

	// ??§?¨?
	private static class Point{
		public final int x;
		public final int y;
		public Point(int x, int y) {
			this.x=x;
			this.y=y;
		}
	}
	// ??§?¨?????????????????????£????????£
	private static Point p(int x, int y){
		return new Point(x, y);
	}

	// ???????????\??????????????????????????????
	private static final Map<Integer, List<Point>> INC = new HashMap<Integer, List<Point>>();
	static{
		List<Point> small = Arrays.asList(p(0,0),p(-1, 0), p(1, 0), p(0, -1), p(0 ,1));//?°????????????????????????????
		List<Point> middle = new ArrayList<Point>(small); // ??\???????????????????????????????????¨???????????????
		middle.addAll(Arrays.asList(p(-1,-1), p(-1, 1), p(1, -1), p(1,1))); //????????????
		List<Point> big = new ArrayList<Point>(middle);
		big.addAll(Arrays.asList(p(2,0), p(-2,0), p(0,2), p(0,-2))); //??§?????????

		INC.put(1, small); 
		INC.put(2, middle); 
		INC.put(3, big);
	};

	public static void main(String[] args) throws Exception{

		int[][] paper = new int[10][10];

		Scanner scan = new Scanner(System.in);

		while(scan.hasNext()) {

			String line = scan.nextLine();
			String[] in = line.split(",");
			paint(paper, Integer.parseInt(in[0]), Integer.parseInt(in[1]), Integer.parseInt(in[2]));
		}
		scan.close();

		// ????¨????????§?
		int whiteCells = 0; //?????????????????°
		int max = 0; // ???????????????

		for (int[] row : paper) {
			for(int cell : row) {
				//?±????????????????????¨?
				if (cell == 0) {
					whiteCells++;
				}
				// ?????§????????´??°
				if (max < cell) {
					max = cell;
				}
			}
		}
		System.out.println(whiteCells);
		System.out.println(max);
	}
	// ?????????????´????????????????
	private static void paint(int[][] paper, int x, int y, int size) {
		int xLen = paper[0].length;
		int yLen = paper.length;
		// INC????????????????????¨??????????????????????????????????????????????????????????????§????§????????????????????????????????????°OK???
		for (Point p : INC.get(size)) {
			if (inPaper(p.x + x, xLen) && inPaper(p.y + y, yLen)){
				paper[p.x + x][p.y + y]++;
			}
		}

	}
	// ?´???????????????????
	private static boolean inPaper(int n, int len) {
		return 0 <= n && n < len;
	}
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main4 {
	static int[][] math;
	static final int MATH_LENGTH = 10;

	public static void main(String[] args) throws IOException {
		//変数の初期化
		math = new int[10][10];
		int x = 0, y = 0;
		int scale = 0;

		//入力値を取得
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		String inputStr = null;
		while (true) {
			inputStr = br.readLine();
			if (inputStr == null || inputStr.isEmpty()) {
				break;
			}
			//入力文字列を","で分割
			String[] inputSplitStr = inputStr.split(",");
			x = Integer.valueOf(inputSplitStr[0]);
			y = Integer.valueOf(inputSplitStr[1]);
			scale = Integer.valueOf(inputSplitStr[2]);

			// インキ滴が滲む範囲の計算
			switch (scale) {
			case 1: //インキ滴「小」
				setSmallInk(x, y);
				break;
			case 2: //インキ滴「中」
				setMediumInk(x, y);
				break;
			case 3: //インキ滴「大」
				setLargeInk(x, y);
				break;
			}
		}

		//塗りつぶしがないマスと最も濃いマスをカウント
		int whiteNumber = 0;
		int mostNumber = 0;
		for (int i = 0; i < MATH_LENGTH; i++) {
			for (int j = 0; j < MATH_LENGTH; j++) {
				if (math[i][j] == 0) {
					whiteNumber++;
				}
				if (mostNumber < math[i][j]) {
					mostNumber = math[i][j];
				}
			}
		}
		System.out.println(whiteNumber);
		System.out.println(mostNumber);
	}

	private static void setSmallInk(int x, int y) {
		//左
		if (0 <= x - 1) {
			math[x - 1][y]++;
		}
		//中上
		if (0 <= y - 1) {
			math[x][y - 1]++;
		}
		//中央
		math[x][y]++;
		//中下
		if (MATH_LENGTH > y + 1) {
			math[x][y + 1]++;
		}
		//右
		if (MATH_LENGTH > x + 1) {
			math[x + 1][y]++;
		}
	}

	/**
	 * [機能] インキ滴「中」のマスの塗りつぶし
	 * 
	 * @param x
	 * @param y
	 */
	private static void setMediumInk(int x, int y) {

		//左
		if (0 <= x - 1) {
			//左中
			math[x - 1][y]++;
			//左上
			if (0 <= y - 1) {
				math[x - 1][y - 1]++;
			}
			//左下
			if (MATH_LENGTH > y + 1) {
				math[x - 1][y + 1]++;
			}
		}

		//中上
		if (0 <= y - 1) {
			math[x][y - 1]++;
		}
		//中央
		math[x][y]++;
		//中下
		if (MATH_LENGTH > y + 1) {
			math[x][y + 1]++;
		}

		//右
		if (MATH_LENGTH > x + 1) {
			math[x + 1][y]++;
			//右上
			if (0 <= y - 1) {
				math[x + 1][y - 1]++;
			}
			//右下
			if (MATH_LENGTH > y + 1) {
				math[x + 1][y + 1]++;
			}
		}
	}

	private static void setLargeInk(int x, int y) {
		//中央
		math[x][y]++;
		//最左
		if (0 <= x - 2) {
			//最左中
			math[x - 2][y]++;
			//左中
			math[x - 1][y]++;
			//左上
			if (0 <= y - 1) {
				math[x - 1][y - 1]++;
			}
			//左下
			if (MATH_LENGTH > y + 1) {
				math[x - 1][y + 1]++;
			}
		} else if (0 <= x - 1) { //左
			//左中
			math[x - 1][y]++;
			//左上
			if (0 <= y - 1) {
				math[x - 1][y - 1]++;
			}
			//左下
			if (MATH_LENGTH > y + 1) {
				math[x - 1][y + 1]++;
			}
		}

		//最右
		if (MATH_LENGTH > x + 2) {
			//最右中
			math[x + 2][y]++;
			//右中
			math[x + 1][y]++;
			//右上
			if (0 <= y - 1) {
				math[x + 1][y - 1]++;
			}
			//右下
			if (MATH_LENGTH > y + 1) {
				math[x + 1][y + 1]++;
			}
		} else if (MATH_LENGTH > x + 1) { //右
			//右中
			math[x + 1][y]++;
			//右上
			if (0 <= y - 1) {
				math[x + 1][y - 1]++;
			}
			//左下
			if (MATH_LENGTH > y + 1) {
				math[x + 1][y + 1]++;
			}
		}

		//最上
		if (0 <= y - 2) {
			//最中上
			math[x][y - 2]++;
			//中上
			math[x][y - 1]++;
		} else if (0 <= y - 1) { //上
			//中上
			math[x][y - 1]++;
		}

		//最下
		if (MATH_LENGTH > y + 2) {
			//最中下
			math[x][y + 2]++;
			//中下
			math[x][y + 1]++;
		} else if (MATH_LENGTH > y + 1) {//中下
			//中下
			math[x][y + 1]++;
		}
	}
}

import java.io.BufferedReader;
