using System;
using System.Collections.Generic;
using System.Linq;

namespace AOJ
{
  class Paper : IEnumerable<int>
  {
    int[,] paper;

    public Paper(int x, int y)
    {
      paper = new int[y,x];
    }

    public int this[int x, int y]
    {
      get
      {
        if (paper.GetLowerBound(1) <= x && x <= paper.GetUpperBound(1) && paper.GetLowerBound(0) <= y && y <= paper.GetUpperBound(0))
          return paper[x, y];
        return -1;
      }
    }

    public void Drop(int x, int y, int size)
    {
        Drop(x, y);
        Drop(x - 1, y);
        Drop(x + 1, y);
        Drop(x, y - 1);
        Drop(x, y + 1);
        if (2 <= size)
        {
          Drop(x - 1, y - 1);
          Drop(x - 1, y + 1);
          Drop(x + 1, y - 1);
          Drop(x + 1, y + 1);
        }
        if (size == 3)
        {
          Drop(x - 2, y);
          Drop(x + 2, y);
          Drop(x, y - 2);
          Drop(x, y + 2);
        }
    }

    public IEnumerator<int> GetEnumerator()
    {
      foreach (var x in Enumerable.Range(0, paper.GetLength(0)))
        foreach (var y in Enumerable.Range(0, paper.GetLength(1)))
          yield return paper[x, y];
    }

    System.Collections.IEnumerator System.Collections.IEnumerable.GetEnumerator()
    {
      return ((System.Collections.IEnumerable)paper).GetEnumerator();
    }

    void Drop(int x, int y)
    {
      if (paper.GetLowerBound(0) <= x && x <= paper.GetUpperBound(0) && paper.GetLowerBound(0) <= y && y <= paper.GetUpperBound(1))
        ++paper[x, y];
    }
  }

  class Problem0026
  {
    static void Main()
    {
      var paper = new Paper(10, 10);
      foreach (var ink in ReadInkPoint())
      {
        paper.Drop(ink[0], ink[1], ink[2]);
      }
      Console.WriteLine(paper.Count(cell => cell == 0));
      Console.WriteLine(paper.Max());
    }

    static IEnumerable<int[]> ReadInkPoint()
    {
      string line;
      while ((line = Console.ReadLine()) != null)
        yield return line.Split(',').Select(s => int.Parse(s)).ToArray();
    }
  }
}

