			if(x != 9 && y != 0) p[x+1, y-1]++;
			if(x != 9 && y != 9) p[x+1, y+1]++;
		}

		if(size >= 3) {
			if(x >= 2)	p[x-2, y]++;
			if(y >= 2)	p[x, y-2]++;
