namespace AOJ.Volume0
{
    public class DroppingInk
    {
        public static void Main()
        {
            // 紙を初期化
            int[,] paper = new int[10, 10];

            // 入力がなくなるまで処理
            while (true)
            {
                string input = Console.ReadLine();
                if (string.IsNullOrEmpty(input)) { break; }
                var data = input.Split(',');
                int x = int.Parse(data[0]);
                int y = int.Parse(data[1]);
                int vol = int.Parse(data[2]);

                // インクの滲み処理
                for (int i = 0; i < 10; i++)
                {
                    for (int j = 0; j < 10; j++)
                    {
                        switch (vol)
                        {
                            case 1:
                                // 小の場合(x, y)からの距離が1以下の位置に滲む
                                if (Math.Abs(x - j) + Math.Abs(y - i) <= 1) { paper[i, j]++; }
                                break;
                            case 2:
                                // 中の場合(x, y)のx方向の距離が1またはy方向の距離が1の位置に滲む
                                if (Math.Abs(x - j) <= 1 || Math.Abs(y - i) <= 1) { paper[i, j]++; }
                                break;
                            case 3:
                                // 大の場合(x, y)からの距離が2以下の位置に滲む
                                if (Math.Abs(x - j) + Math.Abs(y - i) <= 2) { paper[i, j]++; }
                                break;
                        }
                    }
                }
            }
            // 回答
            int white = 0;
            int max = 0;
            for (int i = 0; i < 10; i++)
            {
                for (int j = 0; j < 10; j++)
                {
                    if (paper[i, j] == 0) { white++; }
                    max = Math.Max(max, paper[i, j]);
                }
            }
            Console.WriteLine(white);
            Console.WriteLine(max);
        }
    }
}

