    {
        internal int CountValueIs0In2D(int[][] sheet)
        {
            int zeroCounter = 0;
            for (int i = 0; i < sheet.Length; ++i)
            {
                for (int j = 0; j < sheet[i].Length; ++j)
                {
                    if (sheet[i][j] == 0)
                    {
                        ++zeroCounter;
                    }
                }
            }
            return zeroCounter;
        }
        internal int MaxIn2D(int[][] sheet)
        {
            int max = 0;
            for (int i = 0; i < sheet.Length; ++i)
            {
                for (int j = 0; j < sheet[i].Length; ++j)
                {
                    if (max < sheet[i][j])
                    {
                        max = sheet[i][j];
                    }
                }
            }
            return max;
        }
    }
