                    goto case 2;
                case 2:
                    PaperInc(input[0] - 1, input[1] + 1);
                    PaperInc(input[0] - 1, input[1] - 1);
                    PaperInc(input[0] + 1, input[1] + 1);
                    PaperInc(input[0] + 1, input[1] - 1);
                    goto case 1;
                case 1:
                    PaperInc(input[0] - 1, input[1]);
                    PaperInc(input[0] + 1, input[1]);
                    PaperInc(input[0], input[1] - 1);
                    PaperInc(input[0], input[1] + 1);
                    PaperInc(input[0], input[1]);
                    break;
            }
        }
        static void PaperInc(int x, int y)
        {
            if(x >= 0 && x < 10 && y >= 0 && y < 10) {
                string point = x + " " + y;
                if(!paper.ContainsKey(point)){
                    paper.Add(point, 0);
                }
                ++paper[point];
            }
        }
    }
}

